mapper.select:
/* KitchenMemoMapper.selectKitchenMemo */
SELECT *
  FROM TB_MS_KITCHEN_MEMO
 WHERE STORE_CD = #{orgnCd}
 ORDER
    BY KITCHN_MEMO_CD ASC

/* KitchenMemoMapper.selectKitchenMemoCnt */
SELECT COUNT(1)
  FROM TB_MS_KITCHEN_MEMO
 WHERE STORE_CD = #{storeCd}
   AND KITCHN_MEMO_CD = #{kitchnMemoCd}

mapper.update:
/* KitchenMemoMapper.updateKitchenMemo */
UPDATE TB_MS_KITCHEN_MEMO
   SET KITCHN_MEMO_NM = #{kitchnMemoNm},
       MEMO_FG = #{memoFg},
       USE_YN = #{useYn},
       MOD_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'),
       MOD_ID = #{regId}
 WHERE STORE_CD = #{storeCd}
   AND KITCHN_MEMO_CD = #{kitchnMemoCd}

mapper.delete:
/* KitchenMemoMapper.deleteKitchenMemo */
DELETE TB_MS_KITCHEN_MEMO
 WHERE STORE_CD = #{storeCd}
   AND KITCHN_MEMO_CD = #{kitchnMemoCd}
