mapper.update:

/* CmmGridMapper.updateGridItem */
  UPDATE TB_WB_GRID_DISP_ITEM
     SET COLUMN_ITEM = #{columnItem},
         MOD_DT = #{modDt},
         MOD_ID = #{modId}
   WHERE USER_ID = #{userId}
     AND RESRCE_CD = #{resrceCd}
     AND GRID_IDX = #{gridIdx}

mapper.select:

/* CmmGridMapper.selectGridItem */
  SELECT USER_ID,
		   RESRCE_CD,
		   GRID_IDX,
		   COLUMN_ITEM,
		   REG_DT,
		   REG_ID,
		   MOD_DT,
		   MOD_ID
    FROM TB_WB_GRID_DISP_ITEM
   WHERE USER_ID = #{userId}
     AND RESRCE_CD = #{resrceCd}
     AND GRID_IDX = #{gridIdx}
