mapper.select:
/* CmmMenuMapper.selectStore */
SELECT STORE_CD
  FROM TB_MS_STORE
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}

/* CmmMenuMapper.selectStore */ SELECT STORE_CD, STORE_NM, OWNER_NM, HQ_OFFICE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY REG_DT, MOD_DT DESC
