mapper.select:

  SELECT COLUMN_NAME
    FROM COLS
   WHERE TABLE_NAME = #{table}

SELECT  COM_CD, COM_CD_NM, COM_FG
FROM    TMP_DRAGT_T

WHERE   COM_FG = #{comFg}

mapper.insert:
INSERT INTO TMP_BOARD_T (BOARD_SEQ_NO, BOARD_TITLE, BOARD_CONTENTS, INS_DT , INS_ID)
VALUES
(   ( SELECT  NVL(MAX(BOARD_SEQ_NO),0) + 1 FROM TMP_BOARD_T)
,   #{boardTitle}
,   #{boardContents}
,   TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
,   'test'
)

UPDATE TMP_BOARD_T
SET    BOARD_TITLE = #{boardTitle}
,      BOARD_CONTENTS = #{boardContents}
WHERE  BOARD_SEQ_NO = #{boardSeqNo}

mapper.select:

  SELECT *
    FROM (
            SELECT
                   COUNT(1) OVER (PARTITION BY 1 ORDER BY 1) TOT_CNT,
                   ROWNUM AS RNUM
            ,      A.*
              FROM SSL_TRDTL_T A
             WHERE SHOP_CD    = #{shopCd}
               AND SALE_DATE  = #{saleDate}
               AND POS_NO     = #{posNo}
               AND BILL_NO    = #{billNo}
         )
   WHERE RNUM >= #{prev}
     AND RNUM <= #{next}
