mapper.select:
/* USE : HqManageMapper.list */
SELECT tho.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tho.OWNER_NM, tho.BIZ_NO, tho.BIZ_TYPE_CD, tho.BIZ_ITEM_CD, tho.BIZ_STORE_NM, tho.TEL_NO, tho.FAX_NO, tho.EMAIL_ADDR, tho.HMPG_ADDR, tho.POST_NO, tho.ADDR, tho.ADDR_DTL, tho.AREA_CD, tho.SYS_STAT_FG, tho.SYS_OPEN_DATE, tho.SYS_CLOSURE_DATE, tho.AGENCY_CD, tho.REMARK, tho.CLS_FG, tho.REG_DT, tho.REG_ID, tho.MOD_DT, tho.MOD_ID, NVL(tms.CNT, 0) AS CNT, the.POS_NM FROM TB_HQ_OFFICE tho, ( SELECT HQ_OFFICE_CD, COUNT(HQ_OFFICE_CD) AS CNT FROM TB_MS_STORE WHERE SYS_STAT_FG = '1' GROUP BY HQ_OFFICE_CD ) tms, ( SELECT the.HQ_BRAND_CD AS HQ_OFFICE_CD, tcm.ENVST_NM AS POS_NM FROM TB_HQ_ENVST the, TB_CM_ENVST tcm WHERE the.ENVST_CD = '140' AND tcm.ENVST_CD = the.ENVST_CD ) the WHERE tho.HQ_OFFICE_CD != '00000' AND tms.HQ_OFFICE_CD (+) = tho.HQ_OFFICE_CD AND the.HQ_OFFICE_CD (+) = tho.HQ_OFFICE_CD
AND tho.HQ_OFFICE_CD = #{hqOfficeCd}
AND tho.HQ_OFFICE_NM = #{hqOfficeNm}
AND tho.BIZ_NO = #{bizNo}
AND tho.CLS_FG = #{clsFg}
AND tho.SYS_STAT_FG = #{sysStatFg}
ORDER BY tho.HQ_OFFICE_CD ASC

/* HqManageMapper.chkBizNo */

SELECT COUNT(STORE_CD) CNT
  FROM (
  SELECT HQ_OFFICE_CD STORE_CD
    FROM TB_HQ_OFFICE
   WHERE SYS_STAT_FG <> '9'
     AND BIZ_NO = #{bizNo}
   UNION ALL
  SELECT STORE_CD
    FROM TB_MS_STORE
   WHERE SYS_STAT_FG <> '9'
   AND BIZ_NO = #{bizNo}
       )

/* HqManageMapper.getBizInfoDtl */ SELECT A.OWNER_NM, A.BIZ_NO, A.BIZ_STORE_NM, A.TEL_NO, A.FAX_NO, A.POST_NO, A.ADDR, A.ADDR_DTL, A.AREA_CD, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '092' AND NMCODE_CD = A.AREA_CD) AREA_NM, A.SYS_STAT_FG, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '005' AND NMCODE_CD = A.SYS_STAT_FG) SYS_STAT_FG_NM, A.SYS_OPEN_DATE, A.AGENCY_CD, B.AGENCY_NM, A.CLS_FG, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '003' AND NMCODE_CD = A.CLS_FG) CLS_FG_NM,
A.HQ_OFFICE_CD STORE_CD, A.HQ_OFFICE_NM STORE_NM FROM TB_HQ_OFFICE A, TB_CM_AGENCY B WHERE A.HQ_OFFICE_CD = #{storeCd} AND B.AGENCY_CD = A.AGENCY_CD
A.STORE_CD, A.STORE_NM FROM TB_MS_STORE A WHERE A.STORE_CD = #{storeCd} AND B.AGENCY_CD = A.AGENCY_CD

/* HqManageMapper.chkBizNo */
SELECT 'D'||LPAD(TO_CHAR(NVL(MAX(SUBSTR(HQ_OFFICE_CD, 2, 4)), 0) + 1), 4, '0') HQ_OFFICE_CD FROM TB_HQ_OFFICE WHERE SYS_STAT_FG = '9'
SELECT 'H'||LPAD(TO_CHAR(NVL(MAX(SUBSTR(HQ_OFFICE_CD, 2, 4)), 0) + 1), 4, '0') HQ_OFFICE_CD FROM TB_HQ_OFFICE WHERE SYS_STAT_FG != '9'

/* HqManageMapper.authHqList */

SELECT HQ_OFFICE_CD VALUE,
       HQ_OFFICE_NM NAME
  FROM TB_HQ_OFFICE
 WHERE HQ_OFFICE_CD != #{hqOfficeCd}
   AND SYS_OPEN_DATE <> '2'
   AND SYS_CLOSURE_DATE > SYSDATE
   AND CLS_FG = #{clsFg}

mapper.update:
/* HqManageMapper.copyAuth */
UPDATE TB_WB_USER_INFO
   SET AUTH_GRP_CD = (SELECT B.AUTH_GRP_CD
                   FROM TB_HQ_EMPLOYEE A,
                        TB_WB_USER_INFO B
                  WHERE A.HQ_OFFICE_CD = #{copyHqOfficeCd}
                    AND A.EMP_NO = '0000'
                    AND B.USER_ID = A.USER_ID
                 )
 WHERE USER_ID = (SELECT A.USER_ID
                    FROM TB_HQ_EMPLOYEE A,
                         TB_WB_USER_INFO B
                   WHERE A.HQ_OFFICE_CD = #{hqOfficeCd}
                     AND A.EMP_NO = '0000'
                     AND B.USER_ID = A.USER_ID
                  )

mapper.delete:
/* HqManageMapper.removeAuth */
DELETE TB_WB_AUTHOR_EXCEPT
 WHERE USER_ID = (SELECT B.USER_ID
                    FROM TB_HQ_EMPLOYEE A,
                         TB_WB_USER_INFO B
                   WHERE A.HQ_OFFICE_CD = #{hqOfficeCd}
                     AND A.EMP_NO = '0000'
                     AND B.USER_ID = A.USER_ID)
   AND RESRCE_CD = #{resrceCd}
