mapper.select:
/* USE : SystemCdMapper.getNmcodeGrpCdList */ SELECT NMCODE_GRP_CD, NMCODE_CD, NMCODE_NM, NMCODE_ITEM_1, NMCODE_ITEM_2, USE_COL_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '000'
AND NMCODE_CD LIKE #{nmcodeCd}||'%'
ORDER BY NMCODE_GRP_CD, NMCODE_CD DESC

/* USE : SystemCdMapper.getNmcodeCdList */ SELECT NMCODE_GRP_CD, NMCODE_CD, NMCODE_NM, NMCODE_ITEM_1, NMCODE_ITEM_2 FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = #{nmcodeGrpCd}
AND NMCODE_CD LIKE #{nmcodeCd}||'%'
ORDER BY NMCODE_CD DESC

mapper.update:
    /* USE : SystemCdMapper.updateNmcodeCdList */
    UPDATE
        TB_CM_NMCODE
    SET
        NMCODE_NM = #{nmcodeNm,jdbcType=VARCHAR},
        NMCODE_ITEM_1 = #{nmcodeItem1,jdbcType=VARCHAR},
        NMCODE_ITEM_2 = #{nmcodeItem2,jdbcType=VARCHAR}
        
        , USE_COL_NM = #{useColNm,jdbcType=VARCHAR}
        
    WHERE
        NMCODE_GRP_CD = #{nmcodeGrpCd}
        AND NMCODE_CD = #{nmcodeCd}
