mapper.select:
/* AlimtalkSendStatusMapper.getAlimtalkSendStatusList */
SELECT hmt.MSG_ID, hmt.REG_DT, hmt.S_OGN_CD, hmt.S_OGN_NM, hmt.S_USER_NM, -- hmt.CALLBACK AS S_PHONE_NUMBER, hmt.R_OGN_CD, hmt.R_OGN_NM, -- (CASE WHEN hmt.R_OGN_FG = 'C' THEN (SELECT SBPENC.D(MEMBR_NM) FROM TB_MB_MEMBER WHERE MEMBR_ORGN_CD = hmt.R_OGN_CD AND MEMBR_NO = hmt.CST_NO GROUP BY MEMBR_NM) -- WHEN hmt.R_OGN_FG NOT IN ('C') THEN (SELECT USER_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = hmt.R_OGN_CD AND USER_ID = hmt.R_USER_ID GROUP BY USER_NM) -- ELSE null END -- ) AS R_USER_NM, hmt.PHONE_NUMBER AS R_PHONE_NUMBER, hmt.MSG_TYPE, hmt.RESERVE_YN, hmt.SEND_DATE, hmt.READ_DATE, hmt.SEND_STATUS, (SELECT DECODE(REMARK, NULL, NMCODE_NM, REMARK) FROM TB_AL_ALIMTALK_NMCODE WHERE NMCODE_GRP_CD = '004' AND NMCODE_LEVEL = '2' AND PARENT_CD = '001' AND NMCODE_NM = hmt.RESULT_CD AND USE_YN = 'Y') AS RESULT_NM, -- DECODE(hmt.COMPANY, '1', 'MONO', '2', 'SMTNT', NULL) AS COMPANY, hmt.SUBJECT, hmt.MSG_CONTENT, hmt.ALK_SEND_SEQ, hmt.GUBUN, hmt.REQUEST_ID, hmt.SEND_ORGN_NM, hmt.PAY_ORGN_NM, hmt.SENDER_KEY_ORGN_NM, hmt.RM_SEND_YN, hmt.RM_TYPE, hmt.RM_TITLE, hmt.RM_CONTENT, hmt.RM_SEND_NO
FROM ( SELECT * FROM ( ( --알림톡 SELECT A.MSG_ID, A.NOW_DATE AS REG_DT, A.S_OGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.S_OGN_CD GROUP BY ORGN_NM) AS S_OGN_NM, A.S_USER_ID, (SELECT USER_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE USER_ID = A.S_USER_ID GROUP BY USER_NM) AS S_USER_NM, -- A.CALLBACK, A.R_OGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.R_OGN_CD GROUP BY ORGN_NM) AS R_OGN_NM, A.R_OGN_FG, A.R_USER_ID, A.CST_NO, SBPENC.D(A.TEL_NO) AS PHONE_NUMBER, (CASE WHEN A.RESULT_CD != '1000' AND A.RESEND_STATUS IN('RSC05','RSC04') THEN -4 ELSE -5 END) AS MSG_TYPE, A.SCHEDULE_TYPE AS RESERVE_YN, DECODE(A.SCHEDULE_TYPE, '1', A.SEND_DATE, NULL) AS SEND_DATE, A.RECEIVE_DATE AS READ_DATE, 0 AS SEND_STATUS, null AS RESULT_CD, -- '1' AS COMPANY, --1:KT, 2:SMTNT A.SUBJECT, A.ALK_MSG AS MSG_CONTENT, A.ALK_SEND_SEQ, 'TB_AL_ALIMTALK_SEND_ENC' AS GUBUN, A.REQUEST_ID, A.SEND_ORGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.SEND_ORGN_CD GROUP BY ORGN_NM) AS SEND_ORGN_NM, A.PAY_ORGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.PAY_ORGN_CD GROUP BY ORGN_NM) AS PAY_ORGN_NM, A.SENDER_KEY_ORGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.SENDER_KEY_ORGN_CD GROUP BY ORGN_NM) AS SENDER_KEY_ORGN_NM, A.RM_SEND_YN, A.RM_TYPE, A.RM_TITLE, A.RM_CONTENT, SBPENC.D(A.RM_SEND_NO) AS RM_SEND_NO FROM TB_AL_ALIMTALK_SEND_ENC A WHERE 1=1 AND A.NOW_DATE BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND A.S_OGN_CD LIKE '%' || #{ssOrgnCd} || '%'
AND A.R_OGN_CD LIKE '%' || #{rrOrgnCd} || '%'
AND A.SCHEDULE_TYPE = #{reserveYn}
) UNION ALL ( --알림톡 전송결과 SELECT A.MSG_ID, A.NOW_DATE AS REG_DT, A.S_OGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.S_OGN_CD GROUP BY ORGN_NM) AS S_OGN_NM, A.S_USER_ID, (SELECT USER_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE USER_ID = A.S_USER_ID GROUP BY USER_NM) AS S_USER_NM, -- A.CALLBACK, A.R_OGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.R_OGN_CD GROUP BY ORGN_NM) AS R_OGN_NM, A.R_OGN_FG, A.R_USER_ID, A.CST_NO, SBPENC.D(A.TEL_NO) AS PHONE_NUMBER, 4 AS MSG_TYPE, A.SCHEDULE_TYPE AS RESERVE_YN, DECODE(A.SCHEDULE_TYPE, '1', A.SEND_DATE, NULL) AS SEND_DATE, A.RECEIVE_DATE AS READ_DATE, (CASE WHEN A.SUCC_COUNT = 0 THEN -1 WHEN A.SUCC_COUNT = 1 THEN 3 ELSE null END ) AS SEND_STATUS, --현재는 1건씩 전송해서 A.RESULT_CD, -- '1' AS COMPANY, --1:KT, 2:SMTNT A.SUBJECT, A.ALK_MSG AS MSG_CONTENT, A.ALK_SEND_SEQ, 'TB_AL_ALIMTALK_REPORT' AS GUBUN, A.REQUEST_ID, A.SEND_ORGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.SEND_ORGN_CD GROUP BY ORGN_NM) AS SEND_ORGN_NM, A.PAY_ORGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.PAY_ORGN_CD GROUP BY ORGN_NM) AS PAY_ORGN_NM, A.SENDER_KEY_ORGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = A.SENDER_KEY_ORGN_CD GROUP BY ORGN_NM) AS SENDER_KEY_ORGN_NM, A.RM_SEND_YN, A.RM_TYPE, A.RM_TITLE, A.RM_CONTENT, SBPENC.D(A.RM_SEND_NO) AS RM_SEND_NO FROM TB_AL_ALIMTALK_REPORT A WHERE 1=1 AND A.NOW_DATE BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND A.S_OGN_CD LIKE '%' || #{ssOrgnCd} || '%'
AND A.R_OGN_CD LIKE '%' || #{rrOrgnCd} || '%'
AND A.SCHEDULE_TYPE = #{reserveYn}
) ) ) hmt, ( SELECT twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD, (CASE WHEN twuidv01.ORGN_FG = 'H' THEN (SELECT AGENCY_CD FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = twuidv01.HQ_OFFICE_CD) WHEN twuidv01.ORGN_FG = 'S' THEN (SELECT AGENCY_CD FROM TB_MS_STORE WHERE STORE_CD = twuidv01.STORE_CD) ELSE null END ) AS AGENCY_CD FROM ( SELECT DECODE(twuidv01.ORGN_FG, 'A', 'M', twuidv01.ORGN_FG) AS ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD FROM TB_WB_USER_INFO_DTL_V01 twuidv01 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1
AND twuidv01.AGENCY_CD = #{orgnCd}
AND twuidv01.HQ_OFFICE_CD = #{orgnCd}
AND twuidv01.STORE_CD = #{orgnCd}
AND twuidv01.ORGN_CD = hmt.S_OGN_CD ORDER BY hmt.REG_DT DESC

mapper.update:
/* AlimtalkSendStatusMapper.getAlkSendSeqRecoverSaveUpdate */
UPDATE
HCS_ALK_SEND_REG_T
SET
ALK_SEND_COUNT = (SELECT (ALK_SEND_COUNT-1) FROM HCS_ALK_SEND_REG_T WHERE ALK_SEND_ORGN_CD = #{orgnCd} AND ALK_SEND_SEQ = #{alkSendSeq}),
SEND_QTY = (SELECT (SEND_QTY-1) FROM HCS_ALK_SEND_REG_T WHERE ALK_SEND_ORGN_CD = #{orgnCd} AND ALK_SEND_SEQ = #{alkSendSeq}),
WAIT_QTY = (SELECT (WAIT_QTY-1) FROM HCS_ALK_SEND_REG_T WHERE ALK_SEND_ORGN_CD = #{orgnCd} AND ALK_SEND_SEQ = #{alkSendSeq}),
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND ALK_SEND_ORGN_CD = #{orgnCd}
AND ALK_SEND_SEQ = #{alkSendSeq}

mapper.select:
/* AlimtalkSendStatusMapper.getAlimtalkDaySendStatusList */
SELECT hdt.OGN_CD AS ORGN_CD, twuidv01.ORGN_NM, hdt.SMS_DATE, hdt.SMS_CHARGE_AMT, hdt.SMS_CHARGE_CNT, hdt.ALK_SEND_QTY, hdt.ALK_WAIT_QTY, hdt.ALK_SUCCESS_QTY, hdt.ALK_FAIL_QTY
FROM HCS_DDSMS_T hdt, ( SELECT twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD, (CASE WHEN twuidv01.ORGN_FG = 'H' THEN (SELECT AGENCY_CD FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = twuidv01.HQ_OFFICE_CD) WHEN twuidv01.ORGN_FG = 'S' THEN (SELECT AGENCY_CD FROM TB_MS_STORE WHERE STORE_CD = twuidv01.STORE_CD) ELSE null END ) AS AGENCY_CD FROM ( SELECT DECODE(twuidv01.ORGN_FG, 'A', 'M', twuidv01.ORGN_FG) AS ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD FROM TB_WB_USER_INFO_DTL_V01 twuidv01 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 AND hdt.SMS_DATE BETWEEN #{startDate} AND #{endDate}
AND twuidv01.AGENCY_CD = #{orgnCd}
AND twuidv01.HQ_OFFICE_CD = #{orgnCd}
AND twuidv01.STORE_CD = #{orgnCd}
AND twuidv01.ORGN_CD = hdt.OGN_CD AND (hdt.SMS_CHARGE_CNT != 0 OR hdt.ALK_SEND_QTY != 0 OR hdt.ALK_WAIT_QTY != 0 OR hdt.ALK_SUCCESS_QTY != 0 OR hdt.ALK_FAIL_QTY != 0 ) ORDER BY hdt.OGN_CD, hdt.SMS_DATE DESC
