mapper.select:
/* AlimtalkSendTypeMapper.getAlimtalkSendTypeDetailTemplateList */ SELECT taat.TEMPLATE_GRP_FG, taat.TEMPLATE_CD, taat.TEMPLATE_NM, taat.TEMPLATE_CONTENT, taat.COMMON_FG, (CASE WHEN taat.COMMON_FG = 'C' THEN '공용' ELSE '계정 전용' END) AS COMMON_FG_NM FROM TB_AL_ALIMTALK_TEMPLATE taat WHERE 1=1 AND ( (taat.COMMON_FG = 'C') OR (taat.COMMON_FG = 'S' AND taat.ORGN_CD = #{orgnCd}) ) AND taat.SEND_TYPE_CD = #{sendTypeCd} AND taat.SEND_TYPE_DTL_CD = #{sendTypeDtlCd} AND taat.APPR_FG = 'TSC03' AND NOT EXISTS ( SELECT * FROM TB_AL_ALIMTALK_TYPE_SETTING taats WHERE 1=1 AND taats.ORGN_CD = #{orgnCd}
AND taats.TEMPLATE_GRP_FG = #{templateGrpFg}
AND taats.TEMPLATE_CD = #{templateCd}
AND taats.TEMPLATE_GRP_FG = taat.TEMPLATE_GRP_FG AND taats.TEMPLATE_CD = taat.TEMPLATE_CD ) ORDER BY taat.COMMON_FG, taat.TEMPLATE_GRP_FG, taat.TEMPLATE_CD

/* AlimtalkSendTypeMapper.getAlimtalkIdRegisterAllChk */
SELECT
taa.SENDER_KEY
FROM TB_AL_ALIMTALK taa
WHERE 1=1
AND taa.ORGN_CD = #{orgnCd}

/* AlimtalkSendTypeMapper.getAlimtalkRegisterGroupChk */
SELECT
taag.SENDER_KEY
FROM TB_AL_ALIMTALK_GROUP taag
WHERE 1=1
AND taag.ORGN_CD = #{orgnCd}
AND taag.GROUP_KEY = #{groupSenderKey}
AND taag.SENDER_KEY = #{senderKey}

mapper.update:
/* AlimtalkSendTypeMapper.getAlimtalkSenderSaveUpdate */
UPDATE
TB_AL_ALIMTALK
SET TOKEN = #{token},
SENDER_KEY = #{senderKey},
APPR_FG = #{apprFg},
USE_YN = 'Y',
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND ORGN_CD = #{orgnCd}
AND PLUS_FRIEND_ID = #{plusFriendId}
AND PHONE_NO = #{phoneNo}
