mapper.select:
/* DclzManageMapper.selectDclzManage */ SELECT tpec.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tpec.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tpec.EMP_NO, tme.EMP_NM, tpec.SALE_DATE,
CASE WHEN LENGTH(tpec.COMMUTE_IN_DT) = 14 THEN TO_CHAR(TO_DATE(tpec.COMMUTE_IN_DT, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi:ss') ELSE tpec.COMMUTE_IN_DT END AS COMMUTE_IN_DT, CASE WHEN LENGTH(tpec.COMMUTE_OUT_DT) = 14 THEN TO_CHAR(TO_DATE(tpec.COMMUTE_OUT_DT, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi:ss') ELSE tpec.COMMUTE_OUT_DT END AS COMMUTE_OUT_DT, CASE WHEN LENGTH(tpec.COMMUTE_OUT_DT) = '14' AND LENGTH(tpec.COMMUTE_IN_DT) = '14' THEN CASE WHEN LENGTH(TRUNC(TRUNC((to_date(tpec.COMMUTE_OUT_DT,'YYYYMMDDHH24MISS') - to_date(tpec.COMMUTE_IN_DT,'YYYYMMDDHH24MISS')) * 1440) / 60)) < '2' THEN LPAD((TRUNC(TRUNC((to_date(tpec.COMMUTE_OUT_DT,'YYYYMMDDHH24MISS') - to_date(tpec.COMMUTE_IN_DT,'YYYYMMDDHH24MISS')) * 1440) / 60)), 2,'0') || ':' || LPAD((MOD(TRUNC(REPLACE((to_date(tpec.COMMUTE_OUT_DT,'YYYYMMDDHH24MISS') - to_date(tpec.COMMUTE_IN_DT,'YYYYMMDDHH24MISS')),'-','') * 1440), 60)), 2, '0') ELSE TRUNC(TRUNC((to_date(tpec.COMMUTE_OUT_DT,'YYYYMMDDHH24MISS') - to_date(tpec.COMMUTE_IN_DT,'YYYYMMDDHH24MISS')) * 1440) / 60) || ':' || LPAD((MOD(TRUNC(REPLACE((to_date(tpec.COMMUTE_OUT_DT,'YYYYMMDDHH24MISS') - to_date(tpec.COMMUTE_IN_DT,'YYYYMMDDHH24MISS')),'-','') * 1440), 60)), 2, '0') END ELSE '-' END AS WORK_TIME,
tpec.IN_FG, tpec.REMARK FROM TB_PS_EMPLOYEE_COMMUTE tpec, TB_MS_EMPLOYEE tme, TB_MS_STORE tms WHERE 1=1 AND tpec.STORE_CD = tme.STORE_CD AND tpec.EMP_NO = tme.EMP_NO AND tpec.STORE_CD = tms.STORE_CD AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tpec.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tpec.IN_FG = #{inFg}
AND
tpec.STORE_CD = #{item}
AND tpec.STORE_CD = #{storeCd}
ORDER BY tpec.SALE_DATE DESC, tpec.COMMUTE_IN_DT ASC

/* DclzManageMapper.selectStoreEmployee */
 SELECT tme.EMP_NO AS value,
         tme.EMP_NM AS name
   FROM TB_MS_EMPLOYEE tme
  WHERE tme.STORE_CD = #{storeCd}

mapper.update:
/* DclzManageMapper.updateDclzManage */
UPDATE TB_PS_EMPLOYEE_COMMUTE
   SET COMMUTE_IN_DT = #{commuteInDt},
       COMMUTE_OUT_DT = #{commuteOutDt},
       IN_FG = 'WEB', 
       REMARK = #{remark},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE STORE_CD = #{storeCd}
   AND EMP_NO = #{empNo}
   AND SALE_DATE = #{saleDate}

mapper.delete:
/* DclzManageMapper.deleteDclzManage */
  DELETE
    FROM TB_PS_EMPLOYEE_COMMUTE
   WHERE STORE_CD = #{storeCd}
     AND EMP_NO = #{empNo}
     AND SALE_DATE = #{saleDate}

mapper.select:
/* DclzManageMapper.selectWorkCheck */
SELECT COUNT(*)
  FROM TB_PS_EMPLOYEE_COMMUTE
 WHERE STORE_CD = #{storeCd}
   AND EMP_NO = #{empNo}
   AND SALE_DATE = #{saleDate}
