mapper.insert:
/* USE : AlimtalkMapper.getAlimtalkSave */
MERGE INTO TB_AL_ALIMTALK_MP_LIST_MOMS
USING DUAL
ON
(
    HQ_OFFICE_CD    = #{hqOfficeCd}
AND ALIMTALK_FG     = #{alimtalkFg}
AND MP_NO           = #{mpNo}
)
WHEN MATCHED THEN
UPDATE
SET
    MP_INFO = #{mpInfo}
,   REMARK = #{remark}
,   MOD_DT = #{modDt}
,   MOD_ID = #{modId}
WHEN NOT MATCHED THEN
INSERT (
    HQ_OFFICE_CD
,   STORE_CD
,   ALIMTALK_FG
,   MP_NO
,   MP_INFO
,   REMARK
,   REG_DT
,   REG_ID
,   MOD_DT
,   MOD_ID
) VALUES (
    #{hqOfficeCd}
,   #{storeCd}
,   #{alimtalkFg}
,   #{mpNo}
,   #{mpInfo}
,   #{remark}
,   #{regDt}
,   #{regId}
,   #{modDt}
,   #{modId}
)

mapper.delete:
/* USE : AlimtalkMapper.getAlimtalkDelete */
DELETE  TB_AL_ALIMTALK_MP_LIST_MOMS
WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
AND     ALIMTALK_FG = #{alimtalkFg}
AND     MP_NO = #{mpNo}
