mapper.select:
/* USE : CdKwnMapper.getNmcodeCdKwnList */
SELECT thn.NMCODE_GRP_CD, thn.NMCODE_CD, thn.NMCODE_NM, thn.NMCODE_ITEM_1, thn.NMCODE_ITEM_2, thn.USE_YN FROM TB_HQ_NMCODE thn WHERE thn.HQ_OFFICE_CD = #{hqOfficeCd} AND thn.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND thn.NMCODE_CD LIKE '%'||#{nmcodeCd}||'%'
ORDER BY thn.NMCODE_CD
SELECT tmsn.NMCODE_GRP_CD, tmsn.NMCODE_CD, tmsn.NMCODE_NM, tmsn.NMCODE_ITEM_1, tmsn.NMCODE_ITEM_2, tmsn.USE_YN FROM TB_MS_STORE_NMCODE tmsn WHERE tmsn.STORE_CD = #{storeCd} AND tmsn.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND tmsn.NMCODE_CD LIKE '%'||#{nmcodeCd}||'%'
ORDER BY tmsn.NMCODE_CD

mapper.insert:
/* USE : CdKwnMapper.getNmcodeCdKwnSaveInsert */
INSERT INTO TB_HQ_NMCODE ( HQ_OFFICE_CD, NMCODE_GRP_CD, NMCODE_CD, PARENT_CD, NMCODE_NM, NMCODE_ITEM_1, NMCODE_ITEM_2, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{nmcodeGrpCd}, #{nmcodeCd}, #{nmcodeGrpCd}, #{nmcodeNm}, #{nmcodeItem1,jdbcType=VARCHAR}, #{nmcodeItem2,jdbcType=VARCHAR}, 'Y', #{regDt}, #{regId}, #{modDt}, #{modId} )
INSERT INTO TB_MS_STORE_NMCODE ( STORE_CD, NMCODE_GRP_CD, NMCODE_CD, PARENT_CD, NMCODE_NM, NMCODE_ITEM_1, NMCODE_ITEM_2, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{nmcodeGrpCd}, #{nmcodeCd}, #{nmcodeGrpCd}, #{nmcodeNm}, #{nmcodeItem1,jdbcType=VARCHAR}, #{nmcodeItem2,jdbcType=VARCHAR}, 'Y', #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* USE : CdKwnMapper.getNmcodeCdKwnSaveUpdate */



        UPDATE
        TB_HQ_NMCODE
        SET
        NMCODE_NM = #{nmcodeNm,jdbcType=VARCHAR},
        NMCODE_ITEM_1 = #{nmcodeItem1,jdbcType=VARCHAR},
        NMCODE_ITEM_2 = #{nmcodeItem2,jdbcType=VARCHAR},
        USE_YN = #{useYn}
        WHERE
        HQ_OFFICE_CD = #{hqOfficeCd}
        AND NMCODE_GRP_CD = #{nmcodeGrpCd}
        AND NMCODE_CD = #{nmcodeCd}



        UPDATE
        TB_MS_STORE_NMCODE
        SET
        NMCODE_NM = #{nmcodeNm,jdbcType=VARCHAR},
        NMCODE_ITEM_1 = #{nmcodeItem1,jdbcType=VARCHAR},
        NMCODE_ITEM_2 = #{nmcodeItem2,jdbcType=VARCHAR},
        USE_YN = #{useYn}
        WHERE
        STORE_CD = #{storeCd}
        AND NMCODE_GRP_CD = #{nmcodeGrpCd}
        AND NMCODE_CD = #{nmcodeCd}

mapper.delete:
/* USE : CdKwnMapper.getNmcodeCdKwnSaveDelete */



        DELETE
        FROM
        TB_HQ_NMCODE
        WHERE
        HQ_OFFICE_CD = #{hqOfficeCd}
        AND NMCODE_GRP_CD = #{nmcodeGrpCd}
        AND NMCODE_CD = #{nmcodeCd}



        DELETE
        FROM
        TB_MS_STORE_NMCODE
        WHERE
        STORE_CD = #{storeCd}
        AND NMCODE_GRP_CD = #{nmcodeGrpCd}
        AND NMCODE_CD = #{nmcodeCd}
