mapper.select:
/* StatusMapper.selectStatus */
SELECT tpei.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tpei.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tpei.POS_NO, tpei.ACCNT_FG, tpei.ACCNT_CD, tha.ACCNT_NM, tpei.ACCNT_AMT * DECODE(tpei.ACCNT_FG, 1, 1, -1) AS ACCNT_AMT, tpei.REAMRK
FROM TB_PS_EXACTCALC_IOMONEY tpei, TB_MS_STORE tms, TB_HQ_ACCOUNT tha WHERE tpei.HQ_OFFICE_CD = #{hqOfficeCd} AND tpei.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tpei.STORE_CD IN
#{item}
AND tpei.ACCNT_FG = #{accntFg}
AND tms.HQ_OFFICE_CD = tpei.HQ_OFFICE_CD AND tms.STORE_CD = tpei.STORE_CD AND tha.HQ_OFFICE_CD (+)= tpei.HQ_OFFICE_CD AND tha.ACCNT_FG (+)= tpei.ACCNT_FG AND tha.ACCNT_CD (+)= tpei.ACCNT_CD AND tha.USE_YN (+)= 'Y' ORDER BY tpei.SALE_DATE DESC, tpei.ACCNT_DT ASC
SELECT tpei.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tpei.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tpei.POS_NO, tpei.ACCNT_FG, tpei.ACCNT_CD, tma.ACCNT_NM, tpei.ACCNT_AMT * DECODE(tpei.ACCNT_FG, 1, 1, -1) AS ACCNT_AMT, tpei.REAMRK
FROM TB_PS_EXACTCALC_IOMONEY tpei, TB_MS_STORE tms, TB_MS_ACCOUNT tma WHERE tpei.HQ_OFFICE_CD = #{hqOfficeCd} AND tpei.STORE_CD = #{storeCd} AND tpei.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tpei.ACCNT_FG = #{accntFg}
AND tpei.ACCNT_CD = #{accntCd}
AND tms.HQ_OFFICE_CD = tpei.HQ_OFFICE_CD AND tms.STORE_CD = tpei.STORE_CD AND tma.STORE_CD (+)= tpei.STORE_CD AND tma.ACCNT_FG (+)= tpei.ACCNT_FG AND tma.ACCNT_CD (+)= tpei.ACCNT_CD AND tma.USE_YN (+)= 'Y' ORDER BY tpei.SALE_DATE DESC, tpei.ACCNT_DT ASC

/* StatusMapper.selectAccntList */ SELECT '' AS VALUE, '선택' AS NAME FROM DUAL UNION SELECT ACCNT_CD AS VALUE, ACCNT_NM AS NAME FROM TB_MS_ACCOUNT WHERE STORE_CD = #{storeCd} AND USE_YN = 'Y'
AND ACCNT_FG = #{accntFg}
ORDER BY VALUE NULLS FIRST

