mapper.select:
/* MsgManageMapper.getMsgManageMsgGrpCd */
SELECT LPAD(NVL(MAX(TO_NUMBER(MSG_GRP_CD)), 0) + 1, 2, '0') AS MSG_GRP_CD FROM HCS_MSGGR_T WHERE 1=1 AND OGN_CD = #{orgnCd} AND MSG_GRP_CD BETWEEN '01' AND '80'
SELECT LPAD(NVL(MAX(TO_NUMBER(MSG_GRP_CD)), 80) + 1, 2, '0') AS MSG_GRP_CD FROM HCS_MSGGR_T WHERE 1=1 AND OGN_CD = #{orgnCd} AND MSG_GRP_CD BETWEEN '81' AND '99'

mapper.update:
/* MsgManageMapper.getMsgManageSaveUpdate */
UPDATE
HCS_MSGGR_T
SET
MSG_GRP_NM = #{msgGrpNm},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND MSG_GRP_CD = #{msgGrpCd}

mapper.delete:
/* MsgManageMapper.getMsgManageSaveDelete */
DELETE
HCS_MSGGR_T
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND MSG_GRP_CD = #{msgGrpCd}

mapper.select:
/* MsgManageMapper.getMsgManageDtlList */
SELECT
hst.SEQ_NO,
hst.TITLE,
hst.MESSAGE
FROM HCS_SMSMS_T hst
WHERE 1=1
AND hst.OGN_CD = #{orgnCd}
AND hst.MSG_GRP_CD = #{msgGrpCd}
ORDER BY TO_NUMBER(hst.SEQ_NO)

/* MsgManageMapper.getMsgManageMsgSeqNo */
SELECT
(NVL(MAX(TO_NUMBER(SEQ_NO)), 0) + 1) AS SEQ_NO
FROM HCS_SMSMS_T
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND MSG_GRP_CD = #{msgGrpCd}

mapper.update:
/* MsgManageMapper.getMsgManageDtlSaveUpdate */
UPDATE
HCS_SMSMS_T
SET
TITLE = #{title},
MESSAGE = #{content},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND MSG_GRP_CD = #{msgGrpCd}
AND SEQ_NO = #{seqNo}

mapper.delete:
/* MsgManageMapper.getMsgManageDtlSaveDelete */
DELETE
HCS_SMSMS_T
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND MSG_GRP_CD = #{msgGrpCd}
AND SEQ_NO = #{seqNo}

/* MsgManageMapper.getMsgManageDtlSaveDeleteAll */
DELETE
HCS_SMSMS_T
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND MSG_GRP_CD = #{msgGrpCd}

mapper.select:
/* MsgManageMapper.getMsgManageStoreRegistList */ SELECT tms.HQ_OFFICE_CD, tms.STORE_CD, tms.STORE_NM, tms.SYS_STAT_FG FROM TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.STORE_CD ASC

/* USE : MsgManageMapper.insertMsgGrpToStore   */
{
    CALL PKG_HCS_MSGGR_T_01.SP_PKG_HCS_MSGGR_T_001
  (
    #{orgnCd},
    #{msgGrpCd},
    #{result, mode=OUT, jdbcType=VARCHAR}
  )
}

/* USE : MsgManageMapper.insertMsgSmsToStore   */
{
    CALL PKG_HCS_SMSMS_T_01.SP_PKG_HCS_SMSMS_T_001
  (
    #{orgnCd},
    #{msgGrpCd},
    #{seqNo},
    #{result, mode=OUT, jdbcType=VARCHAR}
  )
}
