mapper.select:
/* SmsChargeMapper.getSmsBaseQtySelect */
SELECT
cst.SMS_QTY
FROM CET_SMSPT_T cst
WHERE 1=1
AND cst.OGN_CD = #{orgnCd}

mapper.update:
/* SmsChargeMapper.getSmsChargeSaveUpdate */
UPDATE CET_PGLOG_T
SET SUCCESS_YN = #{successYn},
RTN_DATE = #{rtnDate},
RTN_TIME = #{rtnTime},
RTN_ID = #{modId},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND CONTROLNO = #{controlno}

mapper.select:
/* SmsChargeMapper.getSmsChargeQtySelect */
SELECT
cpt.SMS_CHARGE_QTY
FROM CET_PGLOG_T cpt
WHERE 1=1
AND cpt.OGN_CD = #{controlno}
