mapper.select:
/* SmsChargeMapper.getSmsBaseAmtSelect */
SELECT
NVL(cst.SMS_AMT, 0) AS SMS_AMT
FROM CET_SMSPT_T cst
WHERE 1=1
AND cst.OGN_CD = #{orgnCd}

mapper.update:
/* SmsChargeMapper.getSmsChargeSaveUpdate */
UPDATE CET_PGLOG_T
SET SUCCESS_YN = #{successYn},
RTN_DATE = #{rtnDate},
RTN_TIME = #{rtnTime},
RTN_ID = #{modId},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND CONTROLNO = #{controlno}

mapper.select:
/* SmsChargeMapper.getSmsChargeAmtSelect */
SELECT
NVL(cpt.CHARGE_AMT, 0) AS CHARGE_AMT
FROM CET_PGLOG_T cpt
WHERE 1=1
AND cpt.CONTROLNO = #{controlno}

/* SmsChargeMapper.getMsgOneAmtSettingList */ SELECT twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM FROM ( SELECT twuidv01.ORGN_FG, -- DECODE(twuidv01.ORGN_FG, 'A', 'M', twuidv01.ORGN_FG) AS ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD FROM TB_WB_USER_INFO_DTL_V01 twuidv01 WHERE 1=1
AND twuidv01.ORGN_CD LIKE '%'||#{srchOrgnCd}||'%'
AND twuidv01.ORGN_NM LIKE '%'||#{srchOrgnNm}||'%'
AND twuidv01.ORGN_FG = #{srchOrgnFg}
AND twuidv01.ORGN_CD NOT IN ('0000000') GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ORDER BY twuidv01.ORGN_FG
