mapper.select:
/* SmsChargeHistMapper.getSmsChargeHistList */
SELECT -- cpt.OGN_CD AS ORGN_CD, twuidv01.ORGN_NM, -- cst.SMS_QTY, ('[' || cpt.OGN_CD || '] ' || twuidv01.ORGN_NM || ' (현재잔여수량 : ' || cst.SMS_QTY || ')') AS ORGN, cpt.CHARGE_DATE, cpt.CHARGE_TIME, cpt.PGRESOURCE, cpt.CONTROLNO, cpt.APPROVALNUM, cpt.CHARGE_AMT, NVL(cpt.SMS_BASE_QTY, 0) AS SMS_BASE_QTY, NVL(cpt.SMS_CHARGE_QTY, 0) AS SMS_CHARGE_QTY, cpt.SUCCESS_YN, (SELECT USER_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE USER_ID = cpt.CHARGE_ID) AS CHARGE_ID_NM, cpt.RESULTMESSAGE
FROM CET_PGLOG_T cpt, CET_SMSPT_T cst, ( SELECT twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD, (CASE WHEN twuidv01.ORGN_FG = 'H' THEN (SELECT AGENCY_CD FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = twuidv01.HQ_OFFICE_CD) WHEN twuidv01.ORGN_FG = 'S' THEN (SELECT AGENCY_CD FROM TB_MS_STORE WHERE STORE_CD = twuidv01.STORE_CD) ELSE null END ) AS AGENCY_CD FROM ( SELECT DECODE(twuidv01.ORGN_FG, 'A', 'M', twuidv01.ORGN_FG) AS ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD FROM TB_WB_USER_INFO_DTL_V01 twuidv01 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 AND cpt.CHARGE_DATE BETWEEN #{startDate} AND #{endDate}
AND cpt.SUCCESS_YN = #{successYn}
AND cpt.PGRESOURCE = #{pgresource}
AND twuidv01.AGENCY_CD = #{orgnCd}
AND twuidv01.HQ_OFFICE_CD = #{orgnCd}
AND twuidv01.STORE_CD = #{orgnCd}
AND cst.OGN_CD (+)= cpt.OGN_CD AND twuidv01.ORGN_CD = cst.OGN_CD ORDER BY cpt.OGN_CD ASC, cpt.CHARGE_DATE DESC, cpt.CHARGE_TIME DESC

/* SmsChargeHistMapper.getSmsChargeRegistList */ SELECT twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.CLS_FG, twuidv01.SYS_STAT_FG, NVL(cst.SMS_QTY, 0) AS SMS_QTY FROM CET_SMSPT_T cst, ( SELECT twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD, (CASE WHEN twuidv01.ORGN_FG = 'H' THEN (SELECT AGENCY_CD FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = twuidv01.HQ_OFFICE_CD) WHEN twuidv01.ORGN_FG = 'S' THEN (SELECT AGENCY_CD FROM TB_MS_STORE WHERE STORE_CD = twuidv01.STORE_CD) ELSE null END ) AS AGENCY_CD, (CASE WHEN twuidv01.ORGN_FG = 'H' THEN (SELECT CLS_FG FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = twuidv01.HQ_OFFICE_CD) WHEN twuidv01.ORGN_FG = 'S' THEN (SELECT CLS_FG FROM TB_MS_STORE WHERE STORE_CD = twuidv01.STORE_CD) ELSE null END ) AS CLS_FG, (CASE WHEN twuidv01.ORGN_FG = 'H' THEN (SELECT SYS_STAT_FG FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = twuidv01.HQ_OFFICE_CD) WHEN twuidv01.ORGN_FG = 'S' THEN (SELECT SYS_STAT_FG FROM TB_MS_STORE WHERE STORE_CD = twuidv01.STORE_CD) ELSE null END ) AS SYS_STAT_FG FROM ( SELECT DECODE(twuidv01.ORGN_FG, 'A', 'M', twuidv01.ORGN_FG) AS ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD FROM TB_WB_USER_INFO_DTL_V01 twuidv01 WHERE 1=1
AND twuidv01.ORGN_CD LIKE '%'||#{srchOrgnCd}||'%'
AND twuidv01.ORGN_NM LIKE '%'||#{srchOrgnNm}||'%'
AND twuidv01.ORGN_FG = #{srchOrgnFg}
GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1
AND twuidv01.CLS_FG = #{clsFg}
AND twuidv01.SYS_STAT_FG = #{sysStatFg}
AND twuidv01.AGENCY_CD = #{orgnCd}
AND twuidv01.HQ_OFFICE_CD = #{orgnCd}
AND twuidv01.STORE_CD = #{orgnCd}
AND cst.OGN_CD (+)= twuidv01.ORGN_CD ORDER BY twuidv01.ORGN_FG, twuidv01.ORGN_CD
