mapper.select:
/* SmsSendHistMapper.getSmsSendHistList */
SELECT hssrt.REG_DT, hssrt.SMS_SEND_ORGN_CD, (SELECT ORGN_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE ORGN_CD = hssrt.SMS_SEND_ORGN_CD GROUP BY ORGN_NM) AS SMS_SEND_ORGN_NM, hssrt.SMS_SEND_SEQ, hssrt.SMS_SEND_COUNT, hssrt.MSG_TYPE, hssrt.SUBJECT, hssrt.MSG_CONTENT, DECODE(hssrt.RESERVE_YN, '1', hssrt.SEND_DATE, NULL) AS SEND_DATE, hssrt.READ_DATE, hssrt.RESERVE_YN, hssrt.SEND_QTY, hssrt.WAIT_QTY, hssrt.SUCCESS_QTY, hssrt.FAIL_QTY
FROM HCS_SMS_SEND_REG_T hssrt, ( SELECT twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD, (CASE WHEN twuidv01.ORGN_FG = 'H' THEN (SELECT AGENCY_CD FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = twuidv01.HQ_OFFICE_CD) WHEN twuidv01.ORGN_FG = 'S' THEN (SELECT AGENCY_CD FROM TB_MS_STORE WHERE STORE_CD = twuidv01.STORE_CD) ELSE null END ) AS AGENCY_CD FROM ( SELECT DECODE(twuidv01.ORGN_FG, 'A', 'M', twuidv01.ORGN_FG) AS ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD FROM TB_WB_USER_INFO_DTL_V01 twuidv01 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 GROUP BY twuidv01.ORGN_FG, twuidv01.ORGN_CD, twuidv01.ORGN_NM, twuidv01.HQ_OFFICE_CD, twuidv01.STORE_CD ) twuidv01 WHERE 1=1 AND hssrt.REG_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND twuidv01.AGENCY_CD = #{orgnCd}
AND twuidv01.HQ_OFFICE_CD = #{orgnCd}
AND hssrt.SUBJECT LIKE '%'||#{subject}||'%'
AND hssrt.MSG_CONTENT LIKE '%'||#{msgContent}||'%'
AND twuidv01.STORE_CD IN
#{item}
AND twuidv01.ORGN_FG IN ('H')
AND twuidv01.ORGN_FG IN ('S')
AND twuidv01.STORE_CD IN
#{item}
AND twuidv01.STORE_CD = #{orgnCd}
AND twuidv01.ORGN_CD = hssrt.SMS_SEND_ORGN_CD ORDER BY hssrt.REG_DT DESC
