mapper.select:
/* SmsTelNoManageMapper.getSmsTelNoManageChk */
SELECT  COUNT(*)
FROM    HCS_SENDM_T
WHERE   SBPENC.D(TEL_NO) = #{telNo}
AND     OGN_CD = #{orgnCd}
AND     USE_YN = 'Y'

mapper.update:
/* SmsTelNoManageMapper.getSmsTelNoManageUpdate */
UPDATE
    HCS_CRTLG_T
SET
    PROC_FG = '1',
    RES_RECV_NUMBER = SBPENC.E(#{telNo}),
    RES_RESULT_CD = #{resCd},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE 1=1
AND CERT_ID = #{certId}
AND OGN_CD = #{orgnCd}

/* SmsTelNoManageMapper.getSmsTelNoManageSaveUpdate */
UPDATE
    HCS_SENDM_T
SET
    USE_SEQ = #{useSeq},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE 1=1
  AND OGN_CD = #{orgnCd}
  AND TEL_NO = SBPENC.E(#{telNo})

mapper.select:
/* SmsTelNoManageMapper.getOrdrIdxx */
SELECT  OGN_CD
FROM    HCS_CRTLG_T
WHERE   CERT_ID = #{certId}

mapper.update:
/* SmsTelNoManageMapper.getSmsTelNoStopSaveUpdate */
UPDATE
HCS_SENDM_T
SET
USE_YN = #{useYn},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND TEL_NO = SBPENC.E(#{telNo})

mapper.select:
/* SmsTelNoManageMapper.getSmsGeneralNoManageList */ SELECT tcasn.ORGN_CD, twuidv01.ORGN_NM, twuidv01.ORGN_FG, tcasn.ADD_FG, (CASE WHEN tcasn.ADD_FG = '1' THEN '다운로드' ELSE null END) AS DOWNLOAD, tcasn.ADD_PROC_FG, SBPENC.D(tcasn.TEL_NO) AS TEL_NO, tcasn.RETURN_REMARK, tcasn.REMARK, tcasn.CERT_ID, tcasn.USER_ID, tcasn.FILE_NM AS FILE_NAME, tcasn.ADD_PROC_FG AS BACK_ADD_PROC_FG, SBPENC.D(tcasn.TEL_NO) AS BACK_TEL_NO, tcasn.RETURN_REMARK AS BACK_RETURN_REMARK, tcasn.REMARK AS BACK_REMARK FROM TB_CM_ADD_SMS_NO tcasn, TB_WB_USER_INFO_DTL_V01 twuidv01 WHERE 1=1
AND tcasn.ORGN_CD LIKE '%'||#{orgnCd}||'%'
AND twuidv01.ORGN_NM LIKE '%'||#{orgnNm}||'%'
AND tcasn.ADD_PROC_FG = #{addProcFg}
AND twuidv01.ORGN_CD = tcasn.ORGN_CD AND twuidv01.USER_ID = tcasn.USER_ID ORDER BY tcasn.ORGN_CD, tcasn.USER_ID, tcasn.CERT_ID

mapper.update:
/* SmsTelNoManageMapper.getSmsGeneralNoManageSaveUpdate */
UPDATE
TB_CM_ADD_SMS_NO
SET
ADD_PROC_FG = #{addProcFg},
TEL_NO = SBPENC.E(#{telNo}),
RETURN_REMARK = #{returnRemark},
REMARK = #{remark},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND ORGN_CD = #{orgnCd}
AND USER_ID = #{userId}
AND CERT_ID = #{certId}

mapper.delete:
/* SmsTelNoManageMapper.getGeneralNoSmsNoSaveDelete */
DELETE
HCS_SENDM_T
WHERE 1=1
AND OGN_CD = #{orgnCd}
AND SBPENC.D(TEL_NO) = #{backTelNo}
