mapper.select:
/* CmmCodeMapper.selectAgencyList */
SELECT AGENCY_CD, AGENCY_NM
  FROM TB_CM_AGENCY

/* CmmCodeMapper.selectVanList */
SELECT VAN_CD, VAN_NM
  FROM TB_CM_VAN_CMPNY

/* CmmCodeMapper.getHqEnvst */
SELECT ENVST_VAL
  FROM TB_HQ_ENVST
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND ENVST_CD = #{envstCd}
   AND USE_YN = 'Y'
