/* USE : CmmCodeMapper.getHqEnvst */
SELECT
    ENVST_VAL
FROM
    TB_HQ_ENVST
WHERE
    HQ_OFFICE_CD = #{hqOfficeCd}
    AND ENVST_CD = #{envstCd}
    AND USE_YN = 'Y'
