mapper.select:
/* CmmEnvMapper.getHqEnvst */
SELECT ENVST_VAL
  FROM TB_HQ_ENVST
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND ENVST_CD = #{envstCd}
   AND USE_YN = 'Y'

/* CmmEnvMapper.getStoreEnvst */
SELECT ENVST_VAL
  FROM TB_MS_STORE_ENVST
 WHERE STORE_CD = #{storeCd}
   AND ENVST_CD = #{envstCd}
   AND USE_YN = 'Y'

/* CmmEnvMapper.getEnvNm */
SELECT ENVST_NM
  FROM TB_CM_ENVST
 WHERE ENVST_CD = #{envstCd}
   AND USE_YN = 'Y'
