mapper.select:
/* USE : CmmMenuMapper.getStoreInfo */ SELECT STORE_CD, STORE_NM, OWNER_NM, HQ_OFFICE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_NM LIKE '%'||#{storeNm}||'%'
AND STORE_CD != #{defaultStoreCd}
ORDER BY REG_DT, MOD_DT DESC

/* USE : CmmMenuMapper.getCmAgencyInfo */ SELECT AGENCY_CD, AGENCY_NM FROM TB_CM_AGENCY
AND ( AGENCY_CD = #{agencyCd} OR AGENCY_NM LIKE '%'||#{agencyCd}||'%' )
ORDER BY REG_DT, MOD_DT DESC

