mapper.select:
/* USE : CmmMenuMapper.getStoreInfo */ SELECT STORE_CD, STORE_NM, OWNER_NM, HQ_OFFICE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_NM LIKE '%'||#{storeNm}||'%'
AND STORE_CD != #{defaultStoreCd}
ORDER BY REG_DT, MOD_DT DESC

/* USE : CmmMenuMapper.getCmAgencyInfo */ SELECT AGENCY_CD, AGENCY_NM FROM TB_CM_AGENCY
AND ( AGENCY_CD = #{agencyCd} OR AGENCY_NM LIKE '%'||#{agencyCd}||'%' )
ORDER BY REG_DT, MOD_DT DESC

/* USE : CmmMenuMapper.getUserMenuList */ SELECT twri.P_RESRCE, twri.RESRCE_CD, twri.RESRCE_NM, twri.URL, tcn.NMCODE_ITEM_2 AS ICON_NM, FN_GET_MENU_CHK_YN(#{userId}, twri.RESRCE_CD) AS CHK_YN FROM TB_WB_RESRCE_INFO twri INNER JOIN ( SELECT twri.RESRCE_CD FROM TB_WB_RESRCE_INFO twri START WITH twri.RESRCE_CD IN ( SELECT twagr.RESRCE_CD FROM TB_WB_AUTHOR_GRP_INFO twagi LEFT OUTER JOIN TB_WB_AUTHOR_GRP_RESRCE twagr ON twagi.AUTH_GRP_CD = twagr.AUTH_GRP_CD AND twagr.USE_YN = 'Y' WHERE twagr.AUTH_GRP_CD = #{authGrpCd} AND twagi.USE_YN = 'Y'
UNION SELECT #{optResrceCd} AS RESRCE_CD FROM DUAL
UNION ALL SELECT twae.RESRCE_CD FROM TB_WB_AUTHOR_EXCEPT_V01 twae WHERE twae.USER_ID = #{userId} AND twae.INCLD_EXCLD_FG = 'I' AND twae.USE_YN = 'Y' AND twae.LOGIN_FG = NVL(#{loginFg}, 'W') MINUS SELECT twae.RESRCE_CD FROM TB_WB_AUTHOR_EXCEPT_V01 twae WHERE twae.USER_ID = #{userId} AND twae.INCLD_EXCLD_FG = 'E' AND twae.USE_YN = 'Y' AND twae.LOGIN_FG = NVL(#{loginFg}, 'W')
AND twae.RESRCE_CD != #{optResrceCd}
MINUS SELECT twae.RESRCE_CD FROM TB_WB_AUTHOR_EXCEPT_V01 twae WHERE 1=1
AND twae.USER_ID = (SELECT USER_ID FROM TB_HQ_EMPLOYEE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = '0000')
AND twae.USER_ID = (SELECT USER_ID FROM TB_MS_EMPLOYEE WHERE STORE_CD = #{storeCd} AND EMP_NO = '0000')
AND twae.INCLD_EXCLD_FG = 'E' AND twae.USE_YN = 'Y' AND twae.LOGIN_FG = NVL(#{loginFg}, 'W')
AND twae.RESRCE_CD != #{optResrceCd}
) AND twri.RESRCE_FG = 'M' AND twri.USE_YN = 'Y' CONNECT BY twri.RESRCE_CD = PRIOR twri.P_RESRCE GROUP BY twri.RESRCE_CD ) auth ON twri.RESRCE_CD = auth.RESRCE_CD LEFT OUTER JOIN TB_CM_NMCODE tcn ON tcn.NMCODE_GRP_CD = '056' AND twri.RESRCE_CD = tcn.NMCODE_ITEM_1 START WITH twri.P_RESRCE = '000000' AND twri.RESRCE_FG = 'M' AND twri.USE_YN = 'Y' CONNECT BY PRIOR twri.RESRCE_CD = twri.P_RESRCE ORDER SIBLINGS BY twri.DISP_IDX ASC, twri.FUNC_FG ASC

/* USE : CmmMenuMapper.getFixedMenuList */ SELECT twri.RESRCE_CD, twri.P_RESRCE, twri.RESRCE_NM, twri.URL FROM TB_WB_FIXING twf LEFT OUTER JOIN TB_WB_RESRCE_INFO twri ON twf.RESRCE_CD = twri.RESRCE_CD INNER JOIN ( SELECT twri.RESRCE_CD FROM TB_WB_RESRCE_INFO twri START WITH twri.RESRCE_CD IN ( SELECT twagr.RESRCE_CD FROM TB_WB_AUTHOR_GRP_INFO twagi LEFT OUTER JOIN TB_WB_AUTHOR_GRP_RESRCE twagr ON twagi.AUTH_GRP_CD = twagr.AUTH_GRP_CD AND twagr.USE_YN = 'Y' WHERE twagr.AUTH_GRP_CD = #{authGrpCd} AND twagi.USE_YN = 'Y'
UNION SELECT #{optResrceCd} AS RESRCE_CD FROM DUAL
UNION ALL SELECT twae.RESRCE_CD FROM TB_WB_AUTHOR_EXCEPT_V01 twae WHERE twae.USER_ID = #{userId} AND twae.INCLD_EXCLD_FG = 'I' AND twae.USE_YN = 'Y' AND twae.LOGIN_FG = NVL(#{loginFg}, 'W') MINUS SELECT twae.RESRCE_CD FROM TB_WB_AUTHOR_EXCEPT_V01 twae WHERE twae.USER_ID = #{userId} AND twae.INCLD_EXCLD_FG = 'E' AND twae.USE_YN = 'Y' AND twae.LOGIN_FG = NVL(#{loginFg}, 'W')
AND twae.RESRCE_CD != #{optResrceCd}
) AND twri.RESRCE_FG = 'M' AND twri.USE_YN = 'Y' CONNECT BY twri.RESRCE_CD = PRIOR twri.P_RESRCE GROUP BY twri.RESRCE_CD ) auth ON twri.RESRCE_CD = auth.RESRCE_CD WHERE twf.USER_ID = #{userId} AND twf.USE_YN = 'Y'

 /* USE : CmmMenuMapper.menuResrceChk */
SELECT COUNT(RESRCE_CD)
  FROM TB_WB_RESRCE_INFO
 WHERE RESRCE_FG = 'M'
   AND DISP_LEVEL = #{dispLevel}
   AND URL IS NOT NULL
   AND USE_YN ='Y'
   AND URL = #{url}

/* USE : CmmMenuMapper.userMenuChkYn */
SELECT FN_GET_MENU_CHK_YN(#{userId}, #{url}) AS CHK_YN
FROM   DUAL

/* CmmMenuMapper.getWebSessionTimeOutLoginIdChk */
 SELECT NVL(COUNT(NMCODE_NM), 0) AS USER_ID_CNT
 FROM TB_CM_NMCODE
 WHERE 1=1
 AND NMCODE_GRP_CD = '231'
 AND NMCODE_NM = #{userId}

/* CmmMenuMapper.getLoginPwdChk */



        SELECT  COUNT(*)
        FROM 	TB_CM_AGENCY
        WHERE 	AGENCY_CD   = #{agencyCd}
        AND     REMARK      = #{confirmPassword}


        SELECT  COUNT(*)
        FROM 	TB_CM_AGENCY
        WHERE 	AGENCY_CD   = #{agencyCd}
        AND     REMARK      = #{confirmPassword}


        SELECT	COUNT(*)
        FROM 	TB_HQ_OFFICE
        WHERE 	HQ_OFFICE_CD    = #{hqOfficeCd}
        AND 	REMARK          = #{confirmPassword}



        SELECT	COUNT(*)
        FROM 	TB_MS_EMPLOYEE
        WHERE 	USER_ID     = #{userId}
        AND 	SALE_PWD    = #{confirmPassword}
