mapper.select:
/* USE : PopupMapper.getVanList */
SELECT VAN_FG,
       VAN_CD,
       VAN_NM
 FROM TB_CM_VAN_CMPNY


      VAN_FG = #{vanFg}


  AND VAN_CD = #{vanCd}


  AND VAN_NM LIKE '%'||#{vanNm}||'%'


ORDER BY VAN_FG, VAN_CD ASC

/* USE : PopupMapper.selectStoreList */
SELECT tms.STORE_CD, tms.STORE_NM
FROM TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tms.STORE_CD LIKE '%'||#{storeCd}|| '%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY tms.STORE_CD

/* USE : PopupMapper.getSearchStoreList */


  SELECT  tms.STORE_CD, tms.STORE_NM



  FROM    TB_MS_STORE tms
  WHERE   tms.HQ_OFFICE_CD =   #{hqOfficeCd}


    AND tms.STORE_CD LIKE '%'||#{storeCd}|| '%'


    AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'


    AND tms.AGENCY_CD = #{agencyCd}

ORDER BY tms.STORE_CD

/* USE : PopupMapper.getProdClassTree */
SELECT thpc.P_PROD_CLASS_CD, thpc.PROD_CLASS_CD, thpc.PROD_CLASS_NM FROM TB_HQ_PRODUCT_CLASS thpc WHERE thpc.HQ_OFFICE_CD = #{hqOfficeCd} START WITH thpc.P_PROD_CLASS_CD = '00000' AND thpc.HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY thpc.P_PROD_CLASS_CD = PRIOR thpc.PROD_CLASS_CD AND thpc.HQ_OFFICE_CD = #{hqOfficeCd}
SELECT tmpc.P_PROD_CLASS_CD, tmpc.PROD_CLASS_CD, tmpc.PROD_CLASS_NM FROM TB_MS_PRODUCT_CLASS tmpc WHERE tmpc.STORE_CD = #{storeCd} START WITH tmpc.P_PROD_CLASS_CD = '00000' AND tmpc.STORE_CD = #{storeCd} CONNECT BY tmpc.P_PROD_CLASS_CD = PRIOR tmpc.PROD_CLASS_CD AND tmpc.STORE_CD = #{storeCd}

/* USE : PopupMapper.getProdClassCdNm */
SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, #{prodClassCd}) AS PROD_CLASS_CD_NM FROM DUAL
SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, #{prodClassCd}) AS PROD_CLASS_CD_NM FROM DUAL

/* USE : PopupMapper.getHqStoreList */
SELECT A.ORGN_FG, A.STORE_CD, A.STORE_NM
FROM ( SELECT 'H' AS ORGN_FG, HQ_OFFICE_CD AS STORE_CD, HQ_OFFICE_NM AS STORE_NM FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = #{hqOfficeCd} UNION ALL SELECT 'S' AS ORGN_FG, STORE_CD, STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD != #{storeCd}
)A WHERE 1=1
AND A.STORE_CD LIKE '%'||#{srchStoreCd}|| '%'
AND A.STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY A.ORGN_FG, A.STORE_CD
