mapper.select:
/* USE : TreePopupTwoMapper.getProdClassTreeTwo */
SELECT thpc.P_PROD_CLASS_CD, thpc.PROD_CLASS_CD, '[' || thpc.PROD_CLASS_CD || ']' || thpc.PROD_CLASS_NM AS PROD_CLASS_NM FROM ( SELECT L_CLASS_CD AS PROD_CLASS_CD , '00000' AS P_PROD_CLASS_CD , L_CLASS_NM AS PROD_CLASS_NM FROM TB_HQ_PRODUCT_LCLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} UNION SELECT M_CLASS_CD AS PROD_CLASS_CD , L_CLASS_CD AS P_PROD_CLASS_CD , M_CLASS_NM AS PROD_CLASS_NM FROM TB_HQ_PRODUCT_MCLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} UNION SELECT S_CLASS_CD AS PROD_CLASS_CD , M_CLASS_CD AS P_PROD_CLASS_CD , S_CLASS_NM AS PROD_CLASS_NM FROM TB_HQ_PRODUCT_SCLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} ) thpc START WITH thpc.P_PROD_CLASS_CD = '00000' CONNECT BY thpc.P_PROD_CLASS_CD = PRIOR thpc.PROD_CLASS_CD
SELECT tmpc.P_PROD_CLASS_CD, tmpc.PROD_CLASS_CD, '[' || tmpc.PROD_CLASS_CD || ']' || tmpc.PROD_CLASS_NM AS PROD_CLASS_NM FROM ( SELECT L_CLASS_CD AS PROD_CLASS_CD , '00000' AS P_PROD_CLASS_CD , L_CLASS_NM AS PROD_CLASS_NM FROM TB_MS_PRODUCT_LCLASS WHERE STORE_CD = #{storeCd} UNION SELECT M_CLASS_CD AS PROD_CLASS_CD , L_CLASS_CD AS P_PROD_CLASS_CD , M_CLASS_NM AS PROD_CLASS_NM FROM TB_MS_PRODUCT_MCLASS WHERE STORE_CD = #{storeCd} UNION SELECT S_CLASS_CD AS PROD_CLASS_CD , M_CLASS_CD AS P_PROD_CLASS_CD , S_CLASS_NM AS PROD_CLASS_NM FROM TB_MS_PRODUCT_SCLASS WHERE STORE_CD = #{storeCd} ) tmpc START WITH tmpc.P_PROD_CLASS_CD = '00000' CONNECT BY tmpc.P_PROD_CLASS_CD = PRIOR tmpc.PROD_CLASS_CD

/* USE : TreePopupTwoMapper.getProdClassTreeArtisee */
SELECT thpc.P_PROD_CLASS_CD, thpc.PROD_CLASS_CD, '[' || thpc.PROD_CLASS_CD || ']' || thpc.PROD_CLASS_NM AS PROD_CLASS_NM FROM TB_HQ_PRODUCT_CLASS thpc WHERE thpc.HQ_OFFICE_CD = #{hqOfficeCd} START WITH thpc.P_PROD_CLASS_CD = '00000' AND thpc.HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY thpc.P_PROD_CLASS_CD = PRIOR thpc.PROD_CLASS_CD AND thpc.HQ_OFFICE_CD = #{hqOfficeCd}
SELECT tmpc.P_PROD_CLASS_CD, tmpc.PROD_CLASS_CD, '[' || tmpc.PROD_CLASS_CD || ']' || tmpc.PROD_CLASS_NM AS PROD_CLASS_NM FROM TB_MS_PRODUCT_CLASS tmpc WHERE tmpc.STORE_CD = #{storeCd} START WITH tmpc.P_PROD_CLASS_CD = '00000' AND tmpc.STORE_CD = #{storeCd} CONNECT BY tmpc.P_PROD_CLASS_CD = PRIOR tmpc.PROD_CLASS_CD AND tmpc.STORE_CD = #{storeCd}

/* USE : TreePopupTwoMapper.getProdClassCdNm */
SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, #{prodClassCd}) AS PROD_CLASS_CD_NM FROM DUAL
SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, #{prodClassCd}) AS PROD_CLASS_CD_NM FROM DUAL
