/* ContentMapper.getSaleMonthList */ SELECT YOIL_NUM, YOIL AS SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT YOIL_NUM, YOIL, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD') -1, 'D') AS YOIL_NUM, TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL, tsdt.REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}) AND tsdt.SALE_DATE BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'yyyyMMdd') AND TO_CHAR(SYSDATE, 'yyyyMMdd') ) GROUP BY YOIL_NUM, YOIL ) UNION ALL ( SELECT TO_CHAR(SYSDATE-level-1, 'D') AS YOIL_NUM, TO_CHAR(SYSDATE-level, 'DY') AS YOIL, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= 7
) ) GROUP BY YOIL_NUM, YOIL ORDER BY YOIL_NUM, YOIL

/* ContentMapper.getSaleMonthStoreList */ SELECT YOIL_NUM, YOIL AS SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT YOIL_NUM, YOIL, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD') -1, 'D') AS YOIL_NUM, TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL, tsdt.REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.STORE_CD = #{storeCd} AND tsdt.SALE_DATE BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'yyyyMMdd') AND TO_CHAR(SYSDATE, 'yyyyMMdd') ) GROUP BY YOIL_NUM, YOIL ) UNION ALL ( SELECT TO_CHAR(SYSDATE-level-1, 'D') AS YOIL_NUM, TO_CHAR(SYSDATE-level, 'DY') AS YOIL, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= 7
) ) GROUP BY YOIL_NUM, YOIL ORDER BY YOIL_NUM, YOIL

/* ContentMapper.getSaleYearList */ SELECT TO_CHAR(TO_DATE(SALE_YM, 'YYYYMM'), 'yy-MM') AS SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT tsmt.SALE_YM, SUM(tsmt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.SALE_YM BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -12), 'yyyyMM') AND TO_CHAR(SYSDATE, 'yyyyMM') GROUP BY tsmt.SALE_YM ) UNION ALL ( SELECT TO_CHAR(ADD_MONTHS(SYSDATE, -level+1), 'yyyyMM') AS SALE_YM, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= 12
) ) GROUP BY SALE_YM ORDER BY SALE_YM

/* ContentMapper.getSaleYearStoreList */ SELECT TO_CHAR(TO_DATE(SALE_YM, 'YYYYMM'), 'yy-MM') AS SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT tsmt.SALE_YM, SUM(tsmt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD = #{storeCd} AND tsmt.SALE_YM BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -12), 'yyyyMM') AND TO_CHAR(SYSDATE, 'yyyyMM') GROUP BY tsmt.SALE_YM ) UNION ALL ( SELECT TO_CHAR(ADD_MONTHS(SYSDATE, -level+1), 'yyyyMM') AS SALE_YM, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= 12
) ) GROUP BY SALE_YM ORDER BY SALE_YM

/* ContentMapper.getSaleProdDayList */ SELECT * FROM ( SELECT * FROM ( SELECT * FROM ( SELECT tsdp.PROD_CD, tmp.PROD_NM, -- SUBSTR(tmp.PROD_NM, 0, 7) || '..' AS PROD_NM, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN (SELECT STORE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}) AND tsdp.SALE_DATE = TO_CHAR(SYSDATE, 'yyyyMMdd') AND tmp.HQ_BRAND_CD = tsdp.HQ_BRAND_CD AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD GROUP BY tsdp.PROD_CD, tmp.PROD_NM ORDER BY SUM(tsdp.REAL_SALE_AMT) DESC ) WHERE 1=1
AND ROWNUM <= 5
) UNION ALL ( SELECT '' AS PROD_CD, '-' AS PROD_NM, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= 5
) ) WHERE 1=1
AND ROWNUM <= 5
ORDER BY REAL_SALE_AMT DESC

