mapper.select:
/* USE : ExceptForwardMapper.getExcpForwardProduct */
SELECT tnsp.SALE_DATE, -- 영업일자 tnsp.STORE_CD, -- 매장코드 tnsp.PROD_CD, -- 상품코드 tmp.PROD_NM, -- 상품명 tmp.PROD_CLASS_CD , -- 상품분류코드 ( SELECT SF_GET_PROD_CLASSES('S', tmp.STORE_CD, tmp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_NM, -- 상품분류명 NVL(tnsp.IN_QTY, 0) AS IN_QTY, -- 입고수량 NVL(tnsp.OUT_QTY, 0) AS OUT_QTY, -- 출고수량(판매+기타출고) NVL(tsdp.TOT_SALE_QTY, 0) AS TOT_SALE_QTY, -- 판매수량 NVL(tnsp.STOCK_QTY, 0) AS STOCK_QTY, -- 현재고 tnsp.SOLD_OUT_YN, -- 품절여부 tnsp.CD_COMPANY, -- ERP 회사코드 tnsp.CD_PARTNER, -- ERP 점포코드 (확인필요) tnsp.CD_PARTNER AS CD_PJT, -- ERP 점포코드 (확인필요) tnsp.CD_ITEM, -- ERP 품목코드 NVL(tnse.QT_IO, 0) AS QT_IO, -- 예외출고 수량 tnse.NO_EGR, -- 기타출고 고유번호 tnsg.NO_IO, -- 입고번호 tnsg.NO_IOLINE, -- 입고항번 '예외출고'AS REGIST
FROM TB_NEOE_STOCK_PROD tnsp, -- POS 현재수량 테이블 TB_SL_DAILY_PROD tsdp, -- 일별, 상품별 판매수량 테이블 (SELECT tnse.CD_COMPANY, tnse.NO_EGR, tnse.DT_IO, tnse.CD_PJT, tnse.CD_ITEM , max(tnse.QT_IO) keep(DENSE_RANK LAST ORDER BY tnse.NO_EGRLINE) AS QT_IO FROM TB_NEOE_SPOS_EGR tnse GROUP BY tnse.CD_COMPANY, tnse.NO_EGR, tnse.DT_IO, tnse.CD_PJT, tnse.CD_ITEM ) tnse, -- 기타출고(예외출고)테이블 TB_NEOE_SPOS_GR tnsg, -- 입고정보 테이블 TB_MS_PRODUCT tmp -- 상품 테이블(분류정보) WHERE 1=1 AND tnsp.STORE_CD = #{storeCd} AND tnsp.SALE_DATE = #{saleDate} AND tnsp.SOLD_OUT_YN = 'N'
AND tnsp.PROD_CD = #{prodCd}
AND tmp.PROD_NM = #{prodNm}
AND tsdp.STORE_CD (+)= tnsp.STORE_CD AND tsdp.PROD_CD (+)= tnsp.PROD_CD AND tnse.CD_COMPANY (+)= tnsp.CD_COMPANY AND tnse.DT_IO (+)= tnsp.SALE_DATE AND tnse.CD_PJT (+)= tnsp.CD_PARTNER AND tnse.CD_ITEM (+)= tnsp.CD_ITEM AND tnsg.CD_COMPANY = tnsp.CD_COMPANY AND tnsg.CD_PJT = tnsp.CD_PARTNER -- 점포코드 AND tnsg.CD_ITEM = tnsp.CD_ITEM AND tnsg.DT_IO = tnsp.SALE_DATE AND tmp.STORE_CD (+)= tnsp.STORE_CD AND tmp.PROD_CD (+)= tnsp.PROD_CD
