mapper.select:
/* UserMapper.selectUserList */
   SELECT A.USER_ID,
          A.MP_NO,
          B.STORE_NM ORGN_NM
     FROM TB_MS_EMPLOYEE A,
          TB_MS_STORE B
    WHERE A.STORE_CD = B.STORE_CD
      AND A.WEB_USE_YN = 'Y'
      AND A.EMP_NM = #{empNm}
  
      AND A.MP_NO = #{mpNo}
  
  
      AND A.USER_ID = #{userId}
  
   UNION ALL
   SELECT A.USER_ID,
          A.MP_NO,
          B.HQ_OFFICE_NM ORGN_NM
     FROM TB_HQ_EMPLOYEE A,
          TB_HQ_OFFICE B
    WHERE A.HQ_OFFICE_CD = B.HQ_OFFICE_CD
      AND A.WEB_USE_YN = 'Y'
      AND A.EMP_NM = #{empNm}
  
      AND A.MP_NO = #{mpNo}
  
  
      AND A.USER_ID = #{userId}
  
   UNION ALL
   SELECT A.USER_ID,
          A.MP_NO,
          B.AGENCY_NM ORGN_NM
     FROM TB_CM_EMPLOYEE A,
          TB_CM_AGENCY B
    WHERE A.AGENCY_CD = B.AGENCY_CD
      AND A.WEB_USE_YN = 'Y'
      AND A.EMP_NM = #{empNm}
  
      AND A.MP_NO = #{mpNo}
  
  
      AND A.USER_ID = #{userId}

mapper.update:
/* AuthMapper.updateUserPwd */
  UPDATE TB_WB_USER_INFO
     SET USER_PWD = #{userPwd},
         LAST_PWD_CHG_DT = #{lastPwdChgDt}
   WHERE USER_ID = #{userId}
