mapper.select:
/* CaptionMsgMapper.getCaptionMsgGrpComboList */
SELECT CAPTION_IMG_CD AS VALUE
,      CAPTION_IMG_NM AS NAME
FROM   TB_HQ_CAPTION_MESSAGE_GROUP
WHERE  HQ_OFFICE_CD = #{hqOfficeCd}
ORDER BY CAPTION_IMG_CD

/* USE : CaptionMsgMapper.chkCaptionMsgId */
SELECT LISTAGG(CAPTION_MSG_ID, ', ')  WITHIN GROUP (ORDER BY CAPTION_MSG_ID) AS CAPTION_MSG_ID
FROM   TB_HQ_CAPTION_MESSAGE
WHERE  HQ_OFFICE_CD = #{hqOfficeCd}
AND    CAPTION_MSG_ID IN

  #{item}

mapper.insert:
/* USE : CaptionMsgMapper.saveCaptionMsg */
MERGE INTO TB_HQ_CAPTION_MESSAGE A
USING DUAL
    ON (
        A.HQ_OFFICE_CD = #{hqOfficeCd}
    AND A.CAPTION_MSG_ID = #{captionMsgId}
    AND A.CAPTION_IMG_CD = #{captionImgCd}
    )
WHEN MATCHED THEN
UPDATE
SET A.CAPTION_MSG_NM      = #{captionMsgNm}
,   A.CAPTION_MSG_GB      = #{captionMsgGb}
,   A.CAPTION_MSG_EN_NM   = #{captionMsgEnNm}
,   A.CAPTION_MSG_CN_NM   = #{captionMsgCnNm}
,   A.CAPTION_MSG_JP_NM   = #{captionMsgJpNm}
,   A.CAPTION_NO          = #{captionNo}
,   A.REMARK              = #{remark}
,   A.MOD_DT              = #{modDt}
,   A.MOD_ID              = #{modId}
WHEN NOT MATCHED THEN
INSERT
(
    A.HQ_OFFICE_CD
,   A.CAPTION_MSG_ID
,   A.CAPTION_IMG_CD
,   A.CAPTION_MSG_NM
,   A.CAPTION_MSG_GB
,   A.CAPTION_MSG_EN_NM
,   A.CAPTION_MSG_CN_NM
,   A.CAPTION_MSG_JP_NM
,   A.CAPTION_NO
,   A.REMARK
,   A.REG_DT
,   A.REG_ID
,   A.MOD_DT
,   A.MOD_ID
)
VALUES
(
    #{hqOfficeCd}
,   #{captionMsgId}
,   #{captionImgCd}
,   #{captionMsgNm}
,   #{captionMsgGb}
,   #{captionMsgEnNm}
,   #{captionMsgCnNm}
,   #{captionMsgJpNm}
,   #{captionNo}
,   #{remark}
,   #{regDt}
,   #{regId}
,   #{modDt}
,   #{modId}
)

mapper.delete:
/* CaptionMsgMapper.deleteCaptionMsg */
DELETE TB_HQ_CAPTION_MESSAGE
WHERE  HQ_OFFICE_CD = #{hqOfficeCd}
AND    CAPTION_MSG_ID = #{captionMsgId}
AND    CAPTION_IMG_CD = #{captionImgCd}

mapper.select:
/* CaptionMsgMapper.getCaptionMsgGrpList */ SELECT A.HQ_OFFICE_CD , A.CAPTION_IMG_CD , A.CAPTION_IMG_NM , A.FILE_DIR , A.FILE_NM , A.FILE_EXT , A.FILE_SIZE , A.FILE_MIME_TYPE , A.FILE_ORG_NM , A.FILE_DESC , A.FILE_CHG_DT , (SELECT COUNT(*) FROM TB_HQ_CAPTION_MESSAGE WHERE HQ_OFFICE_CD = A.HQ_OFFICE_CD AND CAPTION_IMG_CD = A.CAPTION_IMG_CD) AS MSG_CNT FROM TB_HQ_CAPTION_MESSAGE_GROUP A WHERE A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.CAPTION_IMG_CD LIKE '%' || #{captionImgCd} || '%'
AND A.CAPTION_IMG_NM LIKE '%' || #{captionImgNm} || '%'
ORDER BY CAPTION_IMG_CD

/* USE : CaptionMsgMapper.getCaptionImgCd */
SELECT LPAD(NVL(MAX(CAPTION_IMG_CD), 0) +1, 4, '0')
FROM   TB_HQ_CAPTION_MESSAGE_GROUP
WHERE  HQ_OFFICE_CD = #{hqOfficeCd}

mapper.insert:
/* USE : CaptionMsgMapper.saveCaptionMsgGrp */
INSERT INTO TB_HQ_CAPTION_MESSAGE_GROUP
(
    HQ_OFFICE_CD
,   CAPTION_IMG_CD
,   CAPTION_IMG_NM
,   FILE_DIR
,   FILE_NM
,   FILE_EXT
,   FILE_SIZE
,   FILE_MIME_TYPE
,   FILE_ORG_NM
,   FILE_DESC
,   FILE_CHG_DT
,   REG_DT
,   REG_ID
,   MOD_DT
,   MOD_ID
)
VALUES
(
    #{hqOfficeCd}
,   #{captionImgCd}
,   #{captionImgNm}
,   #{fileDir}
,   #{fileNm}
,   #{fileExt}
,   #{fileSize}
,   #{fileMimeType}
,   #{fileOrgNm}
,   #{fileDesc}
,   #{modDt}
,   #{regDt}
,   #{regId}
,   #{modDt}
,   #{modId}
)

/* USE : CaptionMsgMapper.updateCaptionMsgGrp */
MERGE INTO TB_HQ_CAPTION_MESSAGE_GROUP A
USING DUAL
    ON (
        A.HQ_OFFICE_CD = #{hqOfficeCd}
    AND A.CAPTION_IMG_CD = #{captionImgCd}
    )
WHEN MATCHED THEN
UPDATE
SET A.CAPTION_IMG_NM     = #{captionImgNm}

,   A.FILE_DIR           = #{fileDir}
,   A.FILE_NM            = #{fileNm}
,   A.FILE_EXT           = #{fileExt}
,   A.FILE_SIZE          = #{fileSize}
,   A.FILE_MIME_TYPE     = #{fileMimeType}
,   A.FILE_ORG_NM        = #{fileOrgNm}
,   A.FILE_CHG_DT        = #{modDt}

,   A.FILE_DESC          = #{fileDesc}
,   A.MOD_DT             = #{modDt}
,   A.MOD_ID             = #{modId}
WHEN NOT MATCHED THEN
INSERT
(
    A.HQ_OFFICE_CD
,   A.CAPTION_IMG_CD
,   A.CAPTION_IMG_NM
,   A.FILE_DIR
,   A.FILE_NM
,   A.FILE_EXT
,   A.FILE_SIZE
,   A.FILE_MIME_TYPE
,   A.FILE_ORG_NM
,   A.FILE_DESC
,   A.FILE_CHG_DT
,   A.REG_DT
,   A.REG_ID
,   A.MOD_DT
,   A.MOD_ID
)
VALUES
(
    #{hqOfficeCd}
,   #{captionImgCd}
,   #{captionImgNm}
,   #{fileDir}
,   #{fileNm}
,   #{fileExt}
,   #{fileSize}
,   #{fileMimeType}
,   #{fileOrgNm}
,   #{fileDesc}
,   #{modDt}
,   #{regDt}
,   #{regId}
,   #{modDt}
,   #{modId}
)

mapper.delete:
/* CaptionMsgMapper.deleteCaptionMsgGrp */
DELETE TB_HQ_CAPTION_MESSAGE_GROUP
WHERE  HQ_OFFICE_CD = #{hqOfficeCd}
AND    CAPTION_IMG_CD = #{captionImgCd}
