mapper.select:
/* USE : FnkeyCmNmcdMapper.getCmPosFnkeyList */ SELECT tcpf.FNKEY_NO , tcpf.FNKEY_NM , tcpf.FNKEY_FG , tcn.NMCODE_NM AS FNKEY_FG_NM , tcpf.FNKEY_EN_NM , tcpf.FNKEY_CN_NM , tcpf.FNKEY_JP_NM FROM TB_CM_POS_FNKEY tcpf , TB_CM_NMCODE tcn WHERE 1=1 AND tcn.NMCODE_GRP_CD = '026' AND tcpf.FNKEY_FG = tcn.NMCODE_CD
AND tcpf.FNKEY_FG = #{fnkeyFg}
AND tcpf.FNKEY_NO LIKE '%'|| #{fnkeyNo} ||'%'
AND tcpf.FNKEY_NM LIKE '%'|| #{fnkeyNm} ||'%'
ORDER BY tcpf.FNKEY_FG, tcpf.FNKEY_NO

mapper.update:
/* USE : FnkeyCmNmcdMapper.saveCmPosFnkey */
MERGE INTO TB_CM_POS_FNKEY
USING DUAL
ON (
    FNKEY_NO = #{fnkeyNo}
)
WHEN MATCHED THEN
UPDATE
SET
    FNKEY_EN_NM  = #{fnkeyEnNm}
,   FNKEY_CN_NM  = #{fnkeyCnNm}
,   FNKEY_JP_NM  = #{fnkeyJpNm}
,   MOD_DT       = #{modDt}
,   MOD_ID       = #{modId}

/* USE : FnkeyCmNmcdMapper.applyToStoreFnkey */ MERGE INTO TB_MS_STORE_FNKEY A USING ( SELECT FNKEY_NO , FNKEY_EN_NM , FNKEY_CN_NM , FNKEY_JP_NM , #{modDt} AS MOD_DT , #{modId} AS MOD_ID FROM TB_CM_POS_FNKEY WHERE FNKEY_NO IN
#{item}
) B ON ( A.STORE_CD IN (SELECT STORE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD IN ('DS021', 'DS034', 'H0393', 'A0001')) AND A.FNKEY_NO = B.FNKEY_NO ) WHEN MATCHED THEN UPDATE SET A.FNKEY_EN_NM = B.FNKEY_EN_NM , A.FNKEY_CN_NM = B.FNKEY_CN_NM , A.FNKEY_JP_NM = B.FNKEY_JP_NM , A.MOD_DT = B.MOD_DT , A.MOD_ID = B.MOD_ID

mapper.select:
/* USE : FnkeyCmNmcdMapper.getStoreFnkeyList */ SELECT tms.HQ_OFFICE_CD , tmsf.STORE_CD , tms.STORE_NM , tmsf.FNKEY_NO , tmsf.FNKEY_NM , tmsf.FNKEY_FG , tcn.NMCODE_NM AS FNKEY_FG_NM , tmsf.FNKEY_EN_NM , tmsf.FNKEY_CN_NM , tmsf.FNKEY_JP_NM FROM TB_MS_STORE_FNKEY tmsf , TB_CM_NMCODE tcn , TB_MS_STORE tms WHERE 1=1 AND tcn.NMCODE_GRP_CD = '026' AND tmsf.FNKEY_FG = tcn.NMCODE_CD AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmsf.STORE_CD = tms.STORE_CD
AND tmsf.STORE_CD IN (${storeCdQuery})
AND tmsf.FNKEY_FG = #{fnkeyFg}
AND tmsf.FNKEY_NO LIKE '%'|| #{fnkeyNo} ||'%'
AND tmsf.FNKEY_NM LIKE '%'|| #{fnkeyNm} ||'%'
ORDER BY tms.STORE_CD, tmsf.FNKEY_FG, tmsf.FNKEY_NO

mapper.update:
/* USE : FnkeyCmNmcdMapper.saveStoreFnkey */
MERGE INTO TB_MS_STORE_FNKEY
USING DUAL
ON (
      STORE_CD = #{storeCd}
AND   FNKEY_NO = #{fnkeyNo}
)
WHEN MATCHED THEN
UPDATE
SET
    FNKEY_EN_NM  = #{fnkeyEnNm}
,   FNKEY_CN_NM  = #{fnkeyCnNm}
,   FNKEY_JP_NM  = #{fnkeyJpNm}
,   MOD_DT       = #{modDt}
,   MOD_ID       = #{modId}

mapper.select:
/* USE : FnkeyCmNmcdMapper.getCmNmcdList */ SELECT A.NMCODE_GRP_CD , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '000' AND NMCODE_CD = A.NMCODE_GRP_CD) AS NMCODE_GRP_NM , A.NMCODE_CD , A.NMCODE_NM , A.NMCODE_EN_NM , A.NMCODE_CN_NM , A.NMCODE_JP_NM FROM TB_CM_NMCODE A WHERE 1=1
AND A.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND A.NMCODE_CD LIKE '%'|| #{nmcodeCd} ||'%'
AND A.NMCODE_NM LIKE '%'|| #{nmcodeNm} ||'%'
ORDER BY A.NMCODE_GRP_CD, A.NMCODE_CD

mapper.update:
/* USE : FnkeyCmNmcdMapper.saveCmNmcd */
MERGE INTO TB_CM_NMCODE
USING DUAL
ON (
        NMCODE_GRP_CD = #{nmcodeGrpCd}
    AND NMCODE_CD = #{nmcodeCd}
    AND PARENT_CD = #{nmcodeGrpCd}
)
WHEN MATCHED THEN
UPDATE
SET
    NMCODE_EN_NM  = #{nmcodeEnNm}
,   NMCODE_CN_NM  = #{nmcodeCnNm}
,   NMCODE_JP_NM  = #{nmcodeJpNm}
,   MOD_DT        = #{modDt}
,   MOD_ID        = #{modId}
