mapper.select:
/* USE : KioskSideOptionMapper.getKioskCategoryList */ SELECT thkc.HQ_OFFICE_CD , thkc.TU_CLS_TYPE , thkg.TU_CLS_TYPE_NM , thkc.TU_CLS_CD , thkc.TU_CLS_NM , thkc.TU_PAGE , thkc.X , thkc.Y , thkc.WIDTH , thkc.HEIGHT , thkc.INDEX_NO , thkc.CLS_FG , thkc.CLS_MEMO , thkc.TU_M_CLS_FG , thkc.TU_CLS_EN_NM , thkc.TU_CLS_CN_NM , thkc.TU_CLS_JP_NM , NVL(( SELECT NMCODE_ITEM_2 FROM TB_HQ_NMCODE thn WHERE thn.HQ_OFFICE_CD = thkc.HQ_OFFICE_CD AND thn.NMCODE_GRP_CD = '229' AND thn.NMCODE_NM = thkc.TU_CLS_TYPE AND thn.NMCODE_ITEM_1 = thkc.TU_CLS_CD ), 'N') AS STORE_MOD_YN FROM TB_HQ_KIOSK_CLS thkc , TB_HQ_KIOSK_GROUP thkg WHERE thkc.HQ_OFFICE_CD = #{hqOfficeCd} AND thkc.HQ_OFFICE_CD = thkg.HQ_OFFICE_CD(+) AND thkc.TU_CLS_TYPE = thkg.TU_CLS_TYPE(+)
AND thkc.TU_CLS_TYPE = #{tuClsType}
AND thkc.TU_CLS_TYPE NOT IN ('00') ORDER BY thkc.TU_CLS_TYPE, thkc.INDEX_NO

mapper.update:
/* USE : KioskSideOptionMapper.saveKioskCategory */
MERGE INTO TB_HQ_KIOSK_CLS thkc
USING DUAL
ON (
    HQ_OFFICE_CD = #{hqOfficeCd}
AND TU_CLS_TYPE  = #{tuClsType}
AND TU_CLS_CD    = #{tuClsCd}
)
WHEN MATCHED THEN
UPDATE
SET
    TU_CLS_EN_NM  = #{tuClsEnNm}
,   TU_CLS_CN_NM  = #{tuClsCnNm}
,   TU_CLS_JP_NM  = #{tuClsJpNm}
,   MOD_DT        = #{modDt}
,   MOD_ID        = #{modId}

mapper.select:
/* USE : KioskSideOptionMapper.getSideSdselGrpList */ SELECT thpsg.SDSEL_GRP_CD, thpsg.SDSEL_GRP_NM, NVL(thpsg.SDSEL_TYPE_FG, 'C') AS SDSEL_TYPE_FG, thpsg.SDSEL_GRP_EN_NM, thpsg.SDSEL_GRP_CN_NM, thpsg.SDSEL_GRP_JP_NM FROM TB_HQ_PRODUCT_SDSEL_GROUP thpsg WHERE thpsg.HQ_OFFICE_CD = #{hqOfficeCd} AND thpsg.USE_YN = 'Y'
AND thpsg.SDSEL_TYPE_FG = #{sdselTypeFg}
AND thpsg.SDSEL_GRP_CD LIKE '%'|| #{sdselGrpCd} ||'%'
AND thpsg.SDSEL_GRP_NM LIKE '%'|| #{sdselGrpNm} ||'%'
ORDER BY thpsg.SDSEL_GRP_CD

mapper.update:
/* USE : KioskSideOptionMapper.saveSideSdselGrp */
MERGE INTO TB_HQ_PRODUCT_SDSEL_GROUP thpsg
USING DUAL
ON (
    HQ_OFFICE_CD = #{hqOfficeCd}
AND SDSEL_GRP_CD  = #{sdselGrpCd}
)
WHEN MATCHED THEN
UPDATE
SET
    SDSEL_GRP_EN_NM  = #{sdselGrpEnNm}
,   SDSEL_GRP_CN_NM  = #{sdselGrpCnNm}
,   SDSEL_GRP_JP_NM  = #{sdselGrpJpNm}
,   MOD_DT        = #{modDt}
,   MOD_ID        = #{modId}

/* USE : KioskSideOptionMapper.saveSideSdselClass */
MERGE INTO TB_HQ_PRODUCT_SDSEL_CLASS thpsg
USING DUAL
ON (
    HQ_OFFICE_CD = #{hqOfficeCd}
AND SDSEL_CLASS_CD  = #{sdselClassCd}
)
WHEN MATCHED THEN
UPDATE
SET
    SDSEL_CLASS_EN_NM  = #{sdselClassEnNm}
,   SDSEL_CLASS_CN_NM  = #{sdselClassCnNm}
,   SDSEL_CLASS_JP_NM  = #{sdselClassJpNm}
,   MOD_DT        = #{modDt}
,   MOD_ID        = #{modId}

/* USE : KioskSideOptionMapper.saveOptionGrp */
MERGE INTO TB_HQ_PRODUCT_OPTION_GROUP thpog
USING DUAL
ON (
    HQ_OFFICE_CD = #{hqOfficeCd}
AND OPTION_GRP_CD = #{optionGrpCd}
)
WHEN MATCHED THEN
UPDATE
SET
    OPTION_GRP_EN_NM  = #{optionGrpEnNm}
,   OPTION_GRP_CN_NM  = #{optionGrpCnNm}
,   OPTION_GRP_JP_NM  = #{optionGrpJpNm}
,   MOD_DT        = #{modDt}
,   MOD_ID        = #{modId}

/* USE : KioskSideOptionMapper.saveOptionVal */
MERGE INTO TB_HQ_PRODUCT_OPTION_VAL thpov
USING DUAL
ON (
    HQ_OFFICE_CD = #{hqOfficeCd}
AND OPTION_GRP_CD = #{optionGrpCd}
AND OPTION_VAL_CD = #{optionValCd}
)
WHEN MATCHED THEN
UPDATE
SET
    OPTION_VAL_EN_NM  = #{optionValEnNm}
,   OPTION_VAL_CN_NM  = #{optionValCnNm}
,   OPTION_VAL_JP_NM  = #{optionValJpNm}
,   MOD_DT        = #{modDt}
,   MOD_ID        = #{modId}
