mapper.select:
/* USE : PosServiceMapper.getPosTemplateList */
SELECT thpt.PRT_CLASS_CD, thpt.TEMPLT_REG_FG, thpt.TEMPLT_CD, thpt.TEMPLT_NM, thpt.PRT_FORM, thpt.PRT_EN_FORM, thpt.PRT_CN_FORM, thpt.PRT_JP_FORM, thpt.TEMPLT_CD AS APPLY_TEMPLT_CD, thpt.TEMPLT_REG_FG AS APPLY_TEMPLT_REG_FG FROM TB_HQ_PRINT_TEMPL thpt WHERE thpt.HQ_OFFICE_CD = #{hqOfficeCd} AND thpt.PRT_CLASS_CD = #{prtClassCd} ORDER BY thpt.TEMPLT_REG_FG, thpt.TEMPLT_CD
SELECT sub.PRT_CLASS_CD, sub.TEMPLT_REG_FG, sub.TEMPLT_CD, sub.TEMPLT_NM, sub.PRT_FORM, sub.PRT_EN_FORM, sub.PRT_CN_FORM, sub.PRT_JP_FORM, sub.APPLY_TEMPLT_CD, sub.APPLY_TEMPLT_REG_FG FROM ( SELECT tmp.PRT_CLASS_CD, 'S' AS TEMPLT_REG_FG, '000' AS TEMPLT_CD, '실제출력물' AS TEMPLT_NM, tmp.PRT_FORM, tmp.PRT_EN_FORM, tmp.PRT_CN_FORM, tmp.PRT_JP_FORM, tmp.TEMPLT_CD AS APPLY_TEMPLT_CD, tmp.TEMPLT_REG_FG AS APPLY_TEMPLT_REG_FG FROM TB_MS_PRINT tmp WHERE tmp.STORE_CD = #{storeCd} AND tmp.PRT_CLASS_CD = #{prtClassCd} UNION ALL SELECT tmpt.PRT_CLASS_CD, tmpt.TEMPLT_REG_FG, tmpt.TEMPLT_CD, tmpt.TEMPLT_NM, tmpt.PRT_FORM, tmpt.PRT_EN_FORM, tmpt.PRT_CN_FORM, tmpt.PRT_JP_FORM, tmpt.TEMPLT_CD AS APPLY_TEMPLT_CD, tmpt.TEMPLT_REG_FG AS APPLY_TEMPLT_REG_FG FROM TB_MS_PRINT_TEMPL tmpt WHERE tmpt.STORE_CD = #{storeCd} AND tmpt.PRT_CLASS_CD = #{prtClassCd} ) sub ORDER BY (CASE WHEN sub.TEMPLT_REG_FG = 'S' AND sub.TEMPLT_CD = '000' THEN 1 END), sub.TEMPLT_REG_FG, sub.TEMPLT_CD

mapper.insert:
/* USE : PosServiceMapper.insertPosTemplateList */
INSERT INTO TB_HQ_PRINT_TEMPL ( HQ_OFFICE_CD, PRT_CLASS_CD, TEMPLT_REG_FG, TEMPLT_CD, TEMPLT_NM, PRT_FORM, PRT_EN_FORM, PRT_CN_FORM, PRT_JP_FORM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{prtClassCd}, 'H', ( SELECT NVL(TRIM(TO_CHAR(MAX(thpt.TEMPLT_CD) + 1, '000')), '001') FROM TB_HQ_PRINT_TEMPL thpt WHERE thpt.HQ_OFFICE_CD = #{hqOfficeCd} AND thpt.PRT_CLASS_CD = #{prtClassCd} AND thpt.TEMPLT_REG_FG = 'H' ), #{templtNm}, #{prtForm, jdbcType=VARCHAR}, #{prtEnForm, jdbcType=VARCHAR}, #{prtCnForm, jdbcType=VARCHAR}, #{prtJpForm, jdbcType=VARCHAR}, #{regDt}, #{regId}, #{modDt}, #{modId} )
INSERT INTO TB_MS_PRINT_TEMPL ( STORE_CD, PRT_CLASS_CD, TEMPLT_REG_FG, TEMPLT_CD, TEMPLT_NM, PRT_FORM, PRT_EN_FORM, PRT_CN_FORM, PRT_JP_FORM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{prtClassCd}, 'S', ( SELECT NVL(TRIM(TO_CHAR(MAX(tmpt.TEMPLT_CD) + 1, '000')), '001') FROM TB_MS_PRINT_TEMPL tmpt WHERE tmpt.STORE_CD = #{storeCd} AND tmpt.PRT_CLASS_CD = #{prtClassCd} AND tmpt.TEMPLT_REG_FG = 'S' ), #{templtNm}, #{prtForm, jdbcType=VARCHAR}, #{prtEnForm, jdbcType=VARCHAR}, #{prtCnForm, jdbcType=VARCHAR}, #{prtJpForm, jdbcType=VARCHAR}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* USE : PosServiceMapper.updatePosTemplateList */



        UPDATE
            TB_HQ_PRINT_TEMPL
        SET
            TEMPLT_NM = #{templtNm},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
        WHERE
            HQ_OFFICE_CD = #{hqOfficeCd}
            AND PRT_CLASS_CD = #{prtClassCd}
            AND TEMPLT_REG_FG = 'H'
            AND TEMPLT_CD = #{templtCd}



        UPDATE
            TB_MS_PRINT_TEMPL
        SET
            TEMPLT_NM = #{templtNm},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
        WHERE
            STORE_CD = #{storeCd}
            AND PRT_CLASS_CD = #{prtClassCd}
            AND TEMPLT_REG_FG = 'S'
            AND TEMPLT_CD = #{templtCd}

mapper.delete:
/* USE : PosServiceMapper.deletePosTemplateList */



        DELETE
        FROM
            TB_HQ_PRINT_TEMPL
        WHERE
            HQ_OFFICE_CD = #{hqOfficeCd}
            AND PRT_CLASS_CD = #{prtClassCd}
            AND TEMPLT_REG_FG = 'H'
            AND TEMPLT_CD = #{templtCd}



        DELETE
        FROM
            TB_MS_PRINT_TEMPL
        WHERE
            STORE_CD = #{storeCd}
            AND PRT_CLASS_CD = #{prtClassCd}
            AND TEMPLT_REG_FG = 'S'
            AND TEMPLT_CD = #{templtCd}

mapper.select:
/* USE : PosServiceMapper.getRegStoreList */
SELECT  tms.STORE_CD
,       tms.STORE_NM
,       tms.SYS_STAT_FG
FROM    TB_MS_STORE tms
,       TB_MS_STORE_INFO tmsi
WHERE   tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND     tms.STORE_CD = tmsi.STORE_CD(+)

AND     tms.SYS_STAT_FG = #{sysStatFg}


AND     tms.STORE_CD = #{storeCd}


AND     tms.STORE_NM LIKE '%'||#{storeNm}||'%'


    AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때



        -- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회
        AND tms.HQ_BRAND_CD IN

            #{item}




    AND tmsi.MOMS_TEAM = #{momsTeam}


    AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


    AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


    AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


    AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


    AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND tms.BRANCH_CD = #{branchCd}


    AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}


    AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}


    AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}


    AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}


    AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}

ORDER BY tms.STORE_CD ASC
