mapper.select:
/* USE : PosServiceMapper.getPosTemplateList */
SELECT sub.PRT_CLASS_CD, sub.TEMPLT_CD, sub.TEMPLT_REG_FG, sub.TEMPLT_NM, sub.PRT_FORM FROM ( SELECT thp.PRT_CLASS_CD, '000' AS TEMPLT_CD, 'R' AS TEMPLT_REG_FG, '실제출력물' AS TEMPLT_NM, thp.PRT_FORM FROM TB_HQ_PRINT thp WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PRT_CLASS_CD = #{prtClassCd} UNION ALL SELECT thpt.PRT_CLASS_CD, thpt.TEMPLT_CD, thpt.TEMPLT_REG_FG, thpt.TEMPLT_NM, thpt.PRT_FORM FROM TB_HQ_PRINT_TEMPL thpt WHERE thpt.HQ_OFFICE_CD = #{hqOfficeCd} AND thpt.PRT_CLASS_CD = #{prtClassCd} ) sub ORDER BY sub.TEMPLT_CD, sub.TEMPLT_REG_FG DESC
SELECT sub.PRT_CLASS_CD, sub.TEMPLT_CD, sub.TEMPLT_NM, sub.PRT_FORM FROM ( SELECT tmp.PRT_CLASS_CD, '000' AS TEMPLT_CD, '' AS TEMPLT_REG_FG, '실제출력물' AS TEMPLT_NM, tmp.PRT_FORM FROM TB_MS_PRINT tmp WHERE tmp.STORE_CD = #{storeCd} AND tmp.PRT_CLASS_CD = #{prtClassCd} UNION ALL SELECT tmpt.PRT_CLASS_CD, tmpt.TEMPLT_CD, tmpt.TEMPLT_REG_FG, tmpt.TEMPLT_NM, tmpt.PRT_FORM FROM TB_MS_PRINT_TEMPL tmpt WHERE tmpt.STORE_CD = #{storeCd} AND tmpt.PRT_CLASS_CD = #{prtClassCd} ) sub ORDER BY sub.TEMPLT_CD, sub.TEMPLT_REG_FG DESC

mapper.insert:
/* USE : PosServiceMapper.insertPosTemplateList */
INSERT INTO TB_HQ_PRINT_TEMPL ( HQ_OFFICE_CD, PRT_CLASS_CD, TEMPLT_REG_FG, TEMPLT_CD, TEMPLT_NM, PRT_FORM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{prtClassCd}, 'H', ( SELECT NVL(TRIM(TO_CHAR(MAX(thpt.TEMPLT_CD) + 1, '000')), '001') FROM TB_HQ_PRINT_TEMPL thpt WHERE thpt.HQ_OFFICE_CD = #{hqOfficeCd} AND thpt.PRT_CLASS_CD = #{prtClassCd} AND thpt.TEMPLT_REG_FG = 'H' ), #{templtNm}, #{prtForm, jdbcType=VARCHAR}, #{regDt}, #{regId}, #{modDt}, #{modId} )
INSERT INTO TB_MS_PRINT_TEMPL ( STORE_CD, PRT_CLASS_CD, TEMPLT_REG_FG, TEMPLT_CD, TEMPLT_NM, PRT_FORM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{prtClassCd}, 'S', ( SELECT NVL(TRIM(TO_CHAR(MAX(tmpt.TEMPLT_CD) + 1, '000')), '001') FROM TB_MS_PRINT_TEMPL tmpt WHERE tmpt.STORE_CD = #{storeCd} AND tmpt.PRT_CLASS_CD = #{prtClassCd} AND tmpt.TEMPLT_REG_FG = 'S' ), #{templtNm}, #{prtForm, jdbcType=VARCHAR}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* USE : PosServiceMapper.updatePosTemplateList */



        UPDATE
            TB_HQ_PRINT_TEMPL
        SET
            TEMPLT_NM = #{templtNm},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
        WHERE
            HQ_OFFICE_CD = #{hqOfficeCd}
            AND PRT_CLASS_CD = #{prtClassCd}
            AND TEMPLT_REG_FG = 'H'
            AND TEMPLT_CD = #{templtCd}



        UPDATE
            TB_MS_PRINT_TEMPL
        SET
            TEMPLT_NM = #{templtNm},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
        WHERE
            STORE_CD = #{storeCd}
            AND PRT_CLASS_CD = #{prtClassCd}
            AND TEMPLT_REG_FG = 'S'
            AND TEMPLT_CD = #{templtCd}

mapper.insert:
/* USE : PosServiceMapper.savePosTemplate */
MERGE INTO TB_HQ_PRINT_TEMPL thpt USING DUAL ON ( thpt.HQ_OFFICE_CD = #{hqOfficeCd} AND thpt.PRT_CLASS_CD = #{prtClassCd} AND thpt.TEMPLT_REG_FG = 'H' AND thpt.TEMPLT_CD = #{templtCd} ) WHEN MATCHED THEN UPDATE SET thpt.TEMPLT_NM = #{templtNm}, thpt.PRT_FORM = #{prtForm}, thpt.MOD_DT = #{modDt}, thpt.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( HQ_OFFICE_CD, PRT_CLASS_CD, TEMPLT_REG_FG, TEMPLT_CD, TEMPLT_NM, PRT_FORM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{prtClassCd}, 'H', ( SELECT NVL(TRIM(TO_CHAR(MAX(thpt.TEMPLT_CD) + 1, '000')), '001') FROM TB_HQ_PRINT_TEMPL thpt WHERE thpt.HQ_OFFICE_CD = #{hqOfficeCd} AND thpt.PRT_CLASS_CD = #{prtClassCd} AND thpt.TEMPLT_REG_FG = 'H' ), #{templtNm}, #{prtForm}, #{regDt}, #{regId}, #{modDt}, #{modId} )
MERGE INTO TB_MS_PRINT_TEMPL tmpt USING DUAL ON ( tmpt.STORE_CD = #{storeCd} AND tmpt.PRT_CLASS_CD = #{prtClassCd} AND tmpt.TEMPLT_REG_FG = 'S' AND tmpt.TEMPLT_CD = #{templtCd} ) WHEN MATCHED THEN UPDATE SET tmpt.TEMPLT_NM = #{templtNm}, tmpt.PRT_FORM = #{prtForm}, tmpt.MOD_DT = #{modDt}, tmpt.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( STORE_CD, PRT_CLASS_CD, TEMPLT_REG_FG, TEMPLT_CD, TEMPLT_NM, PRT_FORM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{prtClassCd}, 'S', ( SELECT NVL(TRIM(TO_CHAR(MAX(tmpt.TEMPLT_CD) + 1, '000')), '001') FROM TB_MS_PRINT_TEMPL tmpt WHERE tmpt.STORE_CD = #{storeCd} AND tmpt.PRT_CLASS_CD = #{prtClassCd} AND tmpt.TEMPLT_REG_FG = 'S' ), #{templtNm}, #{prtForm}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* USE : PosServiceMapper.updatePosTemplatePrint */



        UPDATE
            TB_HQ_PRINT
        SET
            PRT_FORM = #{prtForm},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
        WHERE
            HQ_OFFICE_CD = #{hqOfficeCd}
            AND PRT_CLASS_CD = #{prtClassCd}



        UPDATE
            TB_MS_PRINT
        SET
            PRT_FORM = #{prtForm},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
        WHERE
            STORE_CD = #{storeCd}
            AND PRT_CLASS_CD = #{prtClassCd}
