/* USE : CouponMapper.getHqProdList */
SELECT p.HQ_OFFICE_CD, p.PROD_CLASS_CD, p.PROD_CLASS_NM, thcp.PROD_CD, NVL(p.PROD_NM,'삭제된 메뉴') AS PROD_NM, p.COST_UPRC, NVL2(thcp.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_HQ_COUPON_PROD thcp LEFT OUTER JOIN (SELECT thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, thpc.PROD_CLASS_NM, thp.PROD_CD, thp.PROD_NM, thp.COST_UPRC FROM TB_HQ_PRODUCT thp, TB_HQ_PRODUCT_CLASS thpc WHERE thp.HQ_OFFICE_CD = THPC.HQ_OFFICE_CD AND thp.PROD_CLASS_CD = THPC.PROD_CLASS_CD AND THP.HQ_OFFICE_CD = #{hqOfficeCd} AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ) p ON thcp.HQ_OFFICE_CD = p.HQ_OFFICE_CD AND thcp.PROD_CD = p.prod_cd WHERE thcp.HQ_OFFICE_CD = #{hqOfficeCd} AND thcp.PAY_CLASS_CD = #{payClassCd} AND thcp.COUPN_CD = #{coupnCd}
AND p.PROD_CD = #{prodCd}
AND p.PROD_NM LIKE '%'||#{prodNm}||'%'
SELECT thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, thpc.PROD_CLASS_NM, thp.PROD_CD, thp.PROD_NM, thp.COST_UPRC, NVL2(thcp.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_HQ_PRODUCT thp, TB_HQ_PRODUCT_CLASS thpc, TB_HQ_COUPON_PROD thcp WHERE thp.USE_YN = 'Y' AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD = thpc.HQ_OFFICE_CD AND thp.PROD_CLASS_CD = thpc.PROD_CLASS_CD AND thcp.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thcp.PAY_CLASS_CD (+)= #{payClassCd} AND thcp.COUPN_CD (+)= #{coupnCd} AND thcp.PROD_CD (+)= thp.PROD_CD
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thcp.PROD_CD IS NULL AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) )

/* USE : CouponMapper.getStoreProdList */
SELECT p.STORE_CD, p.PROD_CLASS_CD, p.PROD_CLASS_NM, tmcp.PROD_CD, NVL(p.PROD_NM,'삭제된 메뉴') AS PROD_NM, p.COST_UPRC, NVL2(tmcp.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_ms_COUPON_PROD tmcp LEFT OUTER JOIN (SELECT tmp.STORE_CD, tmp.PROD_CLASS_CD, tmpc.PROD_CLASS_NM, tmp.PROD_CD, tmp.PROD_NM, tmp.COST_UPRC FROM TB_MS_PRODUCT tmp, TB_MS_PRODUCT_CLASS tmpc WHERE tmp.STORE_CD = tmpc.STORE_CD AND tmp.PROD_CLASS_CD = tmpc.PROD_CLASS_CD AND tmp.STORE_CD = #{storeCd}) p ON tmcp.STORE_CD = p.STORE_CD AND tmcp.PROD_CD = p.prod_cd WHERE tmcp.STORE_CD = #{storeCd} AND tmcp.PAY_CLASS_CD = #{payClassCd} AND tmcp.COUPN_CD = #{coupnCd}
AND p.PROD_CD = #{prodCd}
AND p.PROD_NM LIKE '%'||#{prodNm}||'%'
SELECT tmp.STORE_CD, tmp.PROD_CLASS_CD, tmpc.PROD_CLASS_NM, tmp.PROD_CD, tmp.PROD_NM, tmp.COST_UPRC, NVL2(tmcp.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_MS_PRODUCT tmp, TB_MS_PRODUCT_CLASS tmpc, TB_MS_COUPON_PROD tmcp WHERE tmp.USE_YN = 'Y' AND tmp.STORE_CD = #{storeCd} AND tmp.STORE_CD = tmpc.STORE_CD AND tmp.PROD_CLASS_CD = tmpc.PROD_CLASS_CD AND tmcp.STORE_CD (+)= tmp.STORE_CD AND tmcp.PAY_CLASS_CD (+)= #{payClassCd} AND tmcp.COUPN_CD (+)= #{coupnCd} AND tmcp.PROD_CD (+)= tmp.PROD_CD
AND tmp.PROD_CD = #{prodCd}
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND tmcp.PROD_CD IS NULL

