AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.STORE_CD ASC

/* USE : CouponMapper.getHqExceptProdList */
SELECT p.HQ_OFFICE_CD , p.PROD_CLASS_CD , p.PROD_CLASS_NM , thcpe.PROD_CD , NVL(p.PROD_NM,'삭제된 메뉴') AS PROD_NM , p.COST_UPRC , NVL2(thcpe.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_HQ_COUPON_PROD_EXCEPT thcpe , ( SELECT thp.HQ_OFFICE_CD , thp.PROD_CLASS_CD , thpc.PROD_CLASS_NM , thp.PROD_CD , thp.PROD_NM , thp.COST_UPRC FROM TB_HQ_PRODUCT thp , TB_HQ_PRODUCT_CLASS thpc WHERE thp.HQ_OFFICE_CD = THPC.HQ_OFFICE_CD AND thp.PROD_CLASS_CD = THPC.PROD_CLASS_CD AND THP.HQ_OFFICE_CD = #{hqOfficeCd} AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ) p WHERE thcpe.HQ_OFFICE_CD (+) = p.HQ_OFFICE_CD AND thcpe.PROD_CD = p.prod_cd AND thcpe.HQ_OFFICE_CD = #{hqOfficeCd} AND thcpe.PAY_CLASS_CD = #{payClassCd} AND thcpe.COUPN_CD = #{coupnCd}
AND p.PROD_CD = #{prodCd}
AND p.PROD_NM LIKE '%'||#{prodNm}||'%'
SELECT thp.HQ_OFFICE_CD , thp.PROD_CLASS_CD , thpc.PROD_CLASS_NM , thp.PROD_CD , thp.PROD_NM , thp.COST_UPRC , NVL2(thcpe.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_HQ_PRODUCT thp , TB_HQ_PRODUCT_CLASS thpc , TB_HQ_COUPON_PROD_EXCEPT thcpe WHERE thp.USE_YN = 'Y' AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD = thpc.HQ_OFFICE_CD AND thp.PROD_CLASS_CD = thpc.PROD_CLASS_CD AND thcpe.HQ_OFFICE_CD (+) = thp.HQ_OFFICE_CD AND thcpe.PAY_CLASS_CD (+) = #{payClassCd} AND thcpe.COUPN_CD (+) = #{coupnCd} AND thcpe.PROD_CD (+) = thp.PROD_CD
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thcpe.PROD_CD IS NULL AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) )

