AND thpcv.S_CLASS_CD = #{prodCd}
AND thpcv.S_CLASS_NM LIKE '%'||#{prodNm}||'%'
AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) )
SELECT DISTINCT thpcv.L_CLASS_CD , '[' || thpcv.L_CLASS_CD || ']' || thpcv.L_CLASS_NM AS L_CLASS_NM , thpcv.M_CLASS_CD , '[' || thpcv.M_CLASS_CD || ']' || thpcv.M_CLASS_NM AS M_CLASS_NM , thpcv.S_CLASS_CD , '[' || thpcv.S_CLASS_CD || ']' || thpcv.S_CLASS_NM AS S_CLASS_NM FROM TB_HQ_PRODUCT_CLASS_V02 thpcv , TB_HQ_PRODUCT thp WHERE thpcv.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD = thpcv.HQ_OFFICE_CD AND thp.PROD_CD = thpcv.PROD_CD AND thp.USE_YN = 'Y' AND thpcv.S_CLASS_CD IS NOT NULL
AND thpcv.S_CLASS_CD = #{prodCd}
AND thpcv.S_CLASS_NM LIKE '%'||#{prodNm}||'%'
AND thpcv.S_CLASS_CD NOT IN ( SELECT PROD_CLASS_CD FROM TB_HQ_COUPON_PROD_CLS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND PAY_CLASS_CD = #{payClassCd} AND COUPN_CD = #{coupnCd} ) AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) )

