(
    #{hqOfficeCd}
,   #{payClassCd}
,   #{coupnCd}
,   #{sClassCd}
,   #{regDt}
,   #{regId}
,   #{regDt}
,   #{regId}
)

mapper.delete:
/* USE : CouponMapper.deleteHqCouponProdCls */
DELETE  TB_HQ_COUPON_PROD_CLS
WHERE   HQ_OFFICE_CD    = #{hqOfficeCd}
AND     PAY_CLASS_CD    = #{payClassCd}
AND     PROD_CLASS_CD   = #{sClassCd}

    AND COUPN_CD = #{coupnCd}

/* USE : CouponMapper.deleteHqCouponProd2 */
DELETE  TB_HQ_COUPON_PROD
WHERE   HQ_OFFICE_CD    = #{hqOfficeCd}
AND     PAY_CLASS_CD    = #{payClassCd}
AND     PROD_CD IN	(
                        SELECT  PROD_CD
                        FROM    TB_HQ_PRODUCT
                        WHERE   HQ_OFFICE_CD    = #{hqOfficeCd}
                        AND     PROD_CLASS_CD   = #{sClassCd}
                    )

    AND COUPN_CD = #{coupnCd}

/* USE : CouponMapper.deleteHqCouponProdClsToStore */
DELETE  TB_MS_COUPON_PROD
WHERE   STORE_CD    IN (
                            SELECT 	STORE_CD
                            FROM 	TB_HQ_COUPON_STORE
                            WHERE 	HQ_OFFICE_CD = #{hqOfficeCd}
                            AND 	PAY_CLASS_CD = #{payClassCd}
                            AND  	COUPN_CD 	 = #{coupnCd}
                        )
AND     PROD_CD IN	(
                        SELECT  PROD_CD
                        FROM    TB_HQ_PRODUCT
                        WHERE   HQ_OFFICE_CD    = #{hqOfficeCd}
                        AND     PROD_CLASS_CD   = #{sClassCd}
                    )
AND     PAY_CLASS_CD    = #{payClassCd}
AND 	COUPN_CD        = #{coupnCd}
AND     COUPN_REG_FG    = 'H'

mapper.insert:
/* USE : CouponMapper.mergeMsCouponProd */
MERGE INTO TB_MS_COUPON_PROD A
USING	(
            SELECT 	C.STORE_CD
            ,		'H'  		    AS COUPN_REG_FG
            ,		#{payClassCd}	AS PAY_CLASS_CD
            ,		#{coupnCd}		AS COUPN_CD
            ,		A.PROD_CD
            FROM 	TB_HQ_PRODUCT A
            ,		TB_HQ_COUPON_PROD_CLS B
            ,		(
                        SELECT 	STORE_CD
                        FROM 	TB_HQ_COUPON_STORE
                        WHERE 	HQ_OFFICE_CD = #{hqOfficeCd}
                        AND 	PAY_CLASS_CD = #{payClassCd}
                        AND  	COUPN_CD 	 = #{coupnCd}
                    ) C
            WHERE   A.PROD_CD       = #{prodCd}
            AND 	A.HQ_OFFICE_CD  = #{hqOfficeCd}
            AND 	B.PROD_CLASS_CD = A.PROD_CLASS_CD
            AND 	B.PAY_CLASS_CD  = #{payClassCd}
            AND 	B.COUPN_CD 		= #{coupnCd}
        ) B
    ON 	(
                A.STORE_CD 		= B.STORE_CD
            AND	A.COUPN_REG_FG 	= B.COUPN_REG_FG
            AND	A.PAY_CLASS_CD 	= B.PAY_CLASS_CD
            AND	A.COUPN_CD 		= B.COUPN_CD
            AND	A.PROD_CD 		= B.PROD_CD
        )
    WHEN MATCHED THEN
        UPDATE
        SET		A.MOD_DT = #{modDt}
        ,   	A.MOD_ID = #{modId}
    WHEN NOT MATCHED THEN
        INSERT
        (
            STORE_CD
        ,	COUPN_REG_FG
        ,	PAY_CLASS_CD
        ,	COUPN_CD
        ,	PROD_CD
        ,	REG_DT
        ,	REG_ID
        ,	MOD_DT
        ,	MOD_ID
        )
        VALUES
        (
            B.STORE_CD
        ,	B.COUPN_REG_FG
        ,	B.PAY_CLASS_CD
        ,	B.COUPN_CD
        ,	B.PROD_CD
        ,	#{regDt}
        ,   #{regId}
        ,   #{modDt}
        ,   #{modId}
        )

mapper.select:
/* USE : CouponMapper.getHqExcelProdList */
    SELECT  thp.HQ_OFFICE_CD
    ,       thp.PROD_CLASS_CD
    ,       thp.PROD_CD
    ,       thp.PROD_NM
    FROM    TB_HQ_PRODUCT thp
    ,       TB_HQ_COUPON_PROD thcp
    WHERE   thp.USE_YN = 'Y'
    AND     thp.HQ_OFFICE_CD        = #{hqOfficeCd}
    AND     thcp.HQ_OFFICE_CD   (+) = thp.HQ_OFFICE_CD
    AND     thcp.PAY_CLASS_CD   (+) = #{payClassCd}
    AND     thcp.COUPN_CD       (+) = #{coupnCd}
    AND     thcp.PROD_CD        (+) = thp.PROD_CD

        AND thp.PROD_CD = #{prodCd}


        AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'

    AND (
    ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) )
    OR
    ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 )
    )

mapper.insert:
/* USE : CouponMapper.mergeMsCouponProdExcelUpload */
MERGE INTO TB_MS_COUPON_PROD A
USING	(
            SELECT 	C.STORE_CD
            ,		'H'  		    AS COUPN_REG_FG
            ,		#{payClassCd}	AS PAY_CLASS_CD
            ,		#{coupnCd}		AS COUPN_CD
            ,		A.PROD_CD
            FROM 	TB_HQ_PRODUCT A
            ,		(
                        SELECT 	STORE_CD
                        FROM 	TB_HQ_COUPON_STORE
                        WHERE 	HQ_OFFICE_CD = #{hqOfficeCd}
                        AND 	PAY_CLASS_CD = #{payClassCd}
                        AND  	COUPN_CD 	 = #{coupnCd}
                    ) C
            WHERE   A.PROD_CD       = #{prodCd}
            AND 	A.HQ_OFFICE_CD 	= #{hqOfficeCd}
            AND     A.USE_YN        = 'Y'
        ) B
ON 	(
            A.STORE_CD 		= B.STORE_CD
        AND	A.COUPN_REG_FG 	= B.COUPN_REG_FG
        AND	A.PAY_CLASS_CD 	= B.PAY_CLASS_CD
        AND	A.COUPN_CD 		= B.COUPN_CD
        AND	A.PROD_CD 		= B.PROD_CD
    )
WHEN MATCHED THEN
    UPDATE
    SET		A.MOD_DT = #{modDt}
    ,   	A.MOD_ID = #{modId}
WHEN NOT MATCHED THEN
    INSERT
        (
            STORE_CD
        ,	COUPN_REG_FG
        ,	PAY_CLASS_CD
        ,	COUPN_CD
        ,	PROD_CD
        ,	REG_DT
        ,	REG_ID
        ,	MOD_DT
        ,	MOD_ID
        )
    VALUES
        (
            B.STORE_CD
        ,	B.COUPN_REG_FG
        ,	B.PAY_CLASS_CD
        ,	B.COUPN_CD
        ,	B.PROD_CD
        ,	#{regDt}
        ,   #{regId}
        ,   #{modDt}
        ,   #{modId}
        )
