   AND PAY_TYPE_FG =  #{payTypeFg}

mapper.delete:
/* USE : CouponMapper.deleteStoreCouponClass */
DELETE TB_MS_PAY_METHOD_CLASS
 WHERE STORE_CD = #{storeCd}
   AND PAY_TYPE_FG = #{payTypeFg}
   AND PAY_CLASS_CD = #{payClassCd}

mapper.select:
/* USE : CouponMapper.getHqCouponList */ SELECT HQ_OFFICE_CD, COUPN_CD, COUPN_NM, PAY_CLASS_CD, COUPN_DC_FG, COUPN_DC_RATE, COUPN_DC_AMT, COUPN_APPLY_FG, USE_YN, REG_ID FROM TB_HQ_COUPON WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND PAY_CLASS_CD = #{payClassCd}
AND USE_YN = #{useYn}

/* USE : CouponMapper.getStoreCouponList */ SELECT STORE_CD, COUPN_CD, COUPN_NM, PAY_CLASS_CD, COUPN_DC_FG, COUPN_DC_RATE, COUPN_DC_AMT, COUPN_APPLY_FG, USE_YN, REG_ID FROM TB_MS_COUPON WHERE STORE_CD = #{storeCd}
AND PAY_CLASS_CD = #{payClassCd}
AND USE_YN = #{useYn}

mapper.update:
/* USE : CouponMapper.updateHqCoupon */
UPDATE TB_HQ_COUPON
   SET COUPN_NM = #{coupnNm},
       COUPN_DC_FG = #{coupnDcFg},
       COUPN_DC_RATE = #{coupnDcRate},
       COUPN_DC_AMT = #{coupnDcAmt},
       COUPN_APPLY_FG = #{coupnApplyFg},
       USE_YN = #{useYn},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND PAY_CLASS_CD =  #{payClassCd}
   AND COUPN_CD = #{coupnCd}

mapper.delete:
/* USE : CouponMapper.deleteHqCoupon */
DELETE TB_HQ_COUPON
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
