mapper.select:
/* USE : CouponMapper.getCouponClassList */
SELECT thpmc.HQ_OFFICE_CD, thpmc.PAY_CLASS_CD, thpmc.PAY_TYPE_FG, thpmc.PAY_CLASS_NM, thpmc.SER_NO_YN, thpmc.USE_YN, thpmc.REG_ID, ( SELECT COUNT(thc.COUPN_CD) FROM TB_HQ_COUPON thc WHERE thc.HQ_OFFICE_CD = thpmc.HQ_OFFICE_CD AND thc.PAY_CLASS_CD = thpmc.PAY_CLASS_CD ) AS COUPON_CNT
FROM TB_HQ_PAY_METHOD_CLASS thpmc WHERE thpmc.HQ_OFFICE_CD = #{hqOfficeCd} AND thpmc.PAY_TYPE_FG = #{payTypeFg}
SELECT tmpmc.STORE_CD, tmpmc.PAY_TYPE_FG, tmpmc.PAY_CLASS_CD, tmpmc.PAY_CLASS_NM, tmpmc.SER_NO_YN, tmpmc.USE_YN, tmpmc.REG_ID, ( SELECT COUNT(tmc.COUPN_CD) FROM TB_MS_COUPON tmc WHERE tmc.STORE_CD = tmpmc.STORE_CD AND tmc.PAY_CLASS_CD = tmpmc.PAY_CLASS_CD ) AS COUPON_CNT
FROM TB_MS_PAY_METHOD_CLASS tmpmc WHERE tmpmc.STORE_CD = #{storeCd} AND tmpmc.PAY_TYPE_FG = #{payTypeFg}

/* USE : CouponMapper.getPayMethodClassCd */

    SELECT
        LPAD( NVL(MAX(PAY_CLASS_CD), 0) + 1, 3, '0')
    FROM
        TB_HQ_PAY_METHOD_CLASS
    WHERE
        HQ_OFFICE_CD = #{hqOfficeCd}
        AND PAY_TYPE_FG = #{payTypeFg}


    SELECT
        LPAD( NVL(MAX(PAY_CLASS_CD), 0) + 1, 3, '0')
    FROM
        TB_MS_PAY_METHOD_CLASS
    WHERE
        STORE_CD = #{storeCd}
        AND PAY_TYPE_FG = #{payTypeFg}

mapper.update:
