/* USE : CouponMapper.getHqCouponList */
SELECT thc.HQ_OFFICE_CD, thc.COUPN_CD, thc.COUPN_NM, thc.PAY_CLASS_CD, thc.COUPN_DC_FG, thc.COUPN_DC_RATE, thc.COUPN_DC_AMT, thc.COUPN_APPLY_FG, thc.USE_YN, thc.REG_ID, (SELECT COUNT(thcp.PROD_CD) FROM TB_HQ_COUPON_PROD thcp WHERE thcp.HQ_OFFICE_CD = thc.HQ_OFFICE_CD AND thcp.PAY_CLASS_CD = thc.PAY_CLASS_CD AND thcp.COUPN_CD = thc.COUPN_CD ) AS PROD_CNT, (SELECT COUNT(thcs.STORE_CD) FROM TB_HQ_COUPON_STORE thcs WHERE thcs.HQ_OFFICE_CD = thc.HQ_OFFICE_CD AND thcs.PAY_CLASS_CD = thc.PAY_CLASS_CD AND thcs.COUPN_CD = thc.COUPN_CD ) AS STORE_CNT
FROM TB_HQ_COUPON thc WHERE thc.HQ_OFFICE_CD = #{hqOfficeCd} AND thc.PAY_CLASS_CD = #{payClassCd} AND thc.USE_YN = 'Y'

/* USE : CouponMapper.getStoreCouponList */
SELECT tmc.STORE_CD, tmc.COUPN_CD, tmc.COUPN_NM, tmc.PAY_CLASS_CD, tmc.COUPN_DC_FG, tmc.COUPN_DC_RATE, tmc.COUPN_DC_AMT, tmc.COUPN_APPLY_FG, tmc.USE_YN, tmc.REG_ID, (SELECT COUNT(tmcp.PROD_CD) FROM TB_MS_COUPON_PROD tmcp WHERE tmcp.STORE_CD = tmc.STORE_CD AND tmcp.PAY_CLASS_CD = tmc.PAY_CLASS_CD AND tmcp.COUPN_CD = tmc.COUPN_CD ) AS PROD_CNT
FROM TB_MS_COUPON tmc WHERE tmc.STORE_CD = #{storeCd} AND tmc.PAY_CLASS_CD = #{payClassCd} AND tmc.USE_YN = 'Y'

/* USE : CouponMapper.getCouponCd */

    SELECT
        LPAD( NVL(MAX(COUPN_CD), 0) + 1, 4, '0') AS COUPN_CD
    FROM
        TB_HQ_COUPON
    WHERE
        HQ_OFFICE_CD = #{hqOfficeCd}
        AND PAY_CLASS_CD = #{payClassCd}


    SELECT
        LPAD( NVL(MAX(COUPN_CD), 0) + 1, 4, '0') AS COUPN_CD
    FROM
        TB_MS_COUPON
    WHERE
        STORE_CD = #{storeCd}
        AND PAY_CLASS_CD = #{payClassCd}

