mapper.update:
/* USE : CouponMapper.updateStoreCoupon */
UPDATE
    TB_MS_COUPON
SET
    COUPN_NM = #{coupnNm},
    COUPN_DC_FG = #{coupnDcFg},
    COUPN_DC_RATE = #{coupnDcRate},
    COUPN_DC_AMT = #{coupnDcAmt},
    COUPN_APPLY_FG = #{coupnApplyFg},
    USE_YN = #{useYn},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE
    STORE_CD = #{storeCd}
    AND COUPN_REG_FG =  #{coupnRegFg}
    AND PAY_CLASS_CD =  #{payClassCd}
    AND COUPN_CD = #{coupnCd}

/* USE : CouponMapper.deleteStoreCoupon */
UPDATE
    TB_MS_COUPON
SET
    USE_YN = #{useYn, jdbcType=VARCHAR},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE
    STORE_CD = #{storeCd}
    AND COUPN_REG_FG =  #{coupnRegFg}
    AND PAY_CLASS_CD = #{payClassCd}
    AND COUPN_CD = #{coupnCd}

mapper.select:
/* USE : CouponMapper.getHqProdList */ SELECT thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, thpc.PROD_CLASS_NM, thp.PROD_CD, thp.PROD_NM, thp.COST_UPRC, NVL2(thcp.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_HQ_PRODUCT thp, TB_HQ_PRODUCT_CLASS thpc, TB_HQ_COUPON_PROD thcp WHERE thp.USE_YN = 'Y' AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD = thpc.HQ_OFFICE_CD AND thp.PROD_CLASS_CD = thpc.PROD_CLASS_CD AND thcp.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thcp.PAY_CLASS_CD (+)= #{payClassCd} AND thcp.COUPN_CD (+)= #{coupnCd} AND thcp.PROD_CD (+)= thp.PROD_CD
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thcp.PROD_CD IS NOT NULL
AND thcp.PROD_CD IS NULL

/* USE : CouponMapper.getStoreProdList */ SELECT tmp.STORE_CD, tmp.PROD_CLASS_CD, tmpc.PROD_CLASS_NM, tmp.PROD_CD, tmp.PROD_NM, tmp.COST_UPRC, NVL2(tmcp.PROD_CD, 'Y', 'N') AS REG_YN FROM TB_MS_PRODUCT tmp, TB_MS_PRODUCT_CLASS tmpc, TB_MS_COUPON_PROD tmcp WHERE tmp.USE_YN = 'Y' AND tmp.STORE_CD = #{storeCd} AND tmp.STORE_CD = tmpc.STORE_CD AND tmp.PROD_CLASS_CD = tmpc.PROD_CLASS_CD AND tmcp.STORE_CD (+)= tmp.STORE_CD AND tmcp.PAY_CLASS_CD (+)= #{payClassCd} AND tmcp.COUPN_CD (+)= #{coupnCd} AND tmcp.PROD_CD (+)= tmp.PROD_CD
AND tmcp.PROD_CD IS NOT NULL
AND tmcp.PROD_CD IS NULL

mapper.delete:
