/* USE : CouponMapper.getStoreList */ SELECT tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, NVL2(thcs.STORE_CD, 'Y', 'N') AS REG_YN FROM TB_MS_STORE tms, TB_HQ_OFFICE tho, TB_HQ_COUPON_STORE thcs WHERE tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND thcs.HQ_OFFICE_CD (+)= tms.HQ_OFFICE_CD AND thcs.STORE_CD (+)= tms.STORE_CD AND thcs.PAY_CLASS_CD (+)= #{payClassCd} AND thcs.COUPN_CD (+)= #{coupnCd}
AND tms.STORE_CD = #{srchStoreCd}
AND tms.STORE_NM LIKE '%'||#{srchStoreNm}||'%'
AND thcs.STORE_CD IS NOT NULL
AND thcs.STORE_CD IS NULL

