mapper.select:
/* USE : GiftMapper.getPayMethodClassCd */

SELECT LPAD( NVL(MAX(PAY_CLASS_CD), 0) + 1, 3, '0')
  FROM TB_HQ_PAY_METHOD_CLASS
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND PAY_TYPE_FG = #{payTypeFg}


SELECT LPAD( NVL(MAX(PAY_CLASS_CD), 0) + 1, 3, '0')
  FROM TB_MS_PAY_METHOD_CLASS
 WHERE STORE_CD = #{storeCd}
   AND PAY_TYPE_FG = #{payTypeFg}

mapper.update:
/* USE : GiftMapper.updateHqGiftClass */
UPDATE TB_HQ_PAY_METHOD_CLASS
   SET PAY_CLASS_NM = #{payClassNm},
       SER_NO_YN = #{serNoYn},
       USE_YN = #{useYn},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND PAY_CLASS_CD = #{payClassCd}
   AND PAY_TYPE_FG =  #{payTypeFg}

/* USE : GiftMapper.deleteHqGiftClass */
UPDATE TB_HQ_PAY_METHOD_CLASS
   SET USE_YN = 'N',
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND PAY_CLASS_CD = #{payClassCd}
   AND PAY_TYPE_FG =  #{payTypeFg}

/* USE : GiftMapper.updateStoreGiftClass */
UPDATE TB_MS_PAY_METHOD_CLASS
   SET PAY_CLASS_NM = #{payClassNm},
       SER_NO_YN = #{serNoYn},
       USE_YN = #{useYn},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE STORE_CD = #{storeCd}
   AND PAY_CLASS_CD = #{payClassCd}
   AND PAY_TYPE_FG =  #{payTypeFg}

/* USE : GiftMapper.deleteStoreGiftClass */
UPDATE TB_MS_PAY_METHOD_CLASS
   SET USE_YN = 'N',
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE STORE_CD = #{storeCd}
   AND PAY_TYPE_FG = #{payTypeFg}
   AND PAY_CLASS_CD = #{payClassCd}

mapper.select:
/* USE : GiftMapper.getHqGiftList */
SELECT thg.HQ_OFFICE_CD,
       thg.GIFT_CD,
       thg.GIFT_NM,
       thg.PAY_CLASS_CD,
       thg.GIFT_UPRC,
       thg.USE_YN,
       thg.REG_ID
  FROM TB_HQ_GIFT thg
 WHERE thg.HQ_OFFICE_CD = #{hqOfficeCd}
   AND thg.PAY_CLASS_CD = #{payClassCd}

   AND thg.USE_YN = #{useYn}

/* USE : GiftMapper.getStoreGiftList */
SELECT tmg.STORE_CD,
       tmg.GIFT_CD,
       tmg.GIFT_NM,
       tmg.PAY_CLASS_CD,
       tmg.GIFT_UPRC,
       tmg.USE_YN,
       tmg.REG_ID
  FROM TB_MS_GIFT tmg
 WHERE tmg.STORE_CD = #{storeCd}
   AND tmg.PAY_CLASS_CD = #{payClassCd}

   AND tmg.USE_YN = #{useYn}

/* USE : GiftMapper.getGiftCd */

  SELECT LPAD( NVL(MAX(GIFT_CD), 0) + 1, 4, '0') AS GIFT_CD
    FROM TB_HQ_GIFT
   WHERE HQ_OFFICE_CD = #{hqOfficeCd}


  SELECT LPAD( NVL(MAX(GIFT_CD), 0) + 1, 4, '0') AS GIFT_CD
    FROM TB_MS_GIFT
   WHERE STORE_CD = #{storeCd}

mapper.update:
/* USE : GiftMapper.updateHqGift */
UPDATE TB_HQ_GIFT
   SET GIFT_NM = #{giftNm},
       GIFT_UPRC = #{giftUprc},
       USE_YN = #{useYn},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND PAY_CLASS_CD =  #{payClassCd}
   AND GIFT_CD = #{giftCd}

/* USE : GiftMapper.deleteHqGift */
UPDATE TB_HQ_GIFT
   SET USE_YN = 'N',
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND PAY_CLASS_CD =  #{payClassCd}
   AND GIFT_CD = #{giftCd}

/* USE : GiftMapper.updateStoreGift */
UPDATE TB_MS_GIFT
   SET GIFT_NM = #{giftNm},
       GIFT_UPRC = #{giftUprc},
       USE_YN = #{useYn},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE STORE_CD = #{storeCd}
   AND PAY_CLASS_CD =  #{payClassCd}
   AND GIFT_CD = #{giftCd}

/* USE : GiftMapper.deleteStoreGift */
UPDATE TB_MS_GIFT
   SET USE_YN = #{useYn, jdbcType=VARCHAR},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE STORE_CD = #{storeCd}
   AND PAY_CLASS_CD = #{payClassCd}
   AND GIFT_CD = #{giftCd}
