mapper.select:
/* HqSplyPriceMapper.getHqSplyPriceExcelList */ SELECT thp.PROD_CD, thp.PROD_NM, thp.PROD_CLASS_CD, -- 상품분류코드 thpsp.SALE_UPRC AS HQ_SALE_UPRC, -- 본사판매가 thp.SPLY_UPRC AS HQ_SPLY_UPRC, -- 대표공급가 thp.SPLY_UPRC AS SPLY_UPRC, -- 변경공급가 thp.PRC_CTRL_FG FROM TB_HQ_PRODUCT thp , ( SELECT HQ_OFFICE_CD , PROD_CD , MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd}
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY P_PROD_CLASS_CD = PRIOR PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.HQ_BRAND_CD = #{prodHqBrandCd} -- 선택한 상품브랜드가 있을 때
-- 선택한 상품브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND thp.HQ_BRAND_CD IN
#{item}
AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) AND thpsp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpsp.PROD_CD = thp.PROD_CD ORDER BY thp.PROD_CD

mapper.insert:
/* USE : HqSplyPriceMapper.saveHqSplyPrice */
MERGE INTO TB_HQ_PRODUCT thp
USING DUAL
ON (
        thp.HQ_OFFICE_CD = #{hqOfficeCd}
        AND thp.PROD_CD = #{prodCd}
    )
WHEN MATCHED THEN
UPDATE
SET     thp.SPLY_UPRC = #{splyUprc}   -- 공급가
,       thp.MOD_DT = #{modDt}
,       thp.MOD_ID = #{modId}

/* USE : HqSplyPriceMapper.saveStoreSplyPrice */ MERGE INTO TB_MS_PRODUCT tmp USING ( SELECT STORE_CD , #{prodCd} AS PROD_CD , #{splyUprc} AS SPLY_UPRC , #{modDt} AS MOD_DT , #{modId} AS MOD_ID FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD NOT IN (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '164' AND USE_YN = 'Y')
AND STORE_CD IN (${storeCdQuery})
) B ON ( tmp.STORE_CD = B.STORE_CD AND tmp.PROD_CD = B.PROD_CD ) WHEN MATCHED THEN UPDATE SET tmp.SPLY_UPRC = B.SPLY_UPRC -- 공급가 , tmp.MOD_DT = B.MOD_DT , tmp.MOD_ID = B.MOD_ID

mapper.select:
/* HqSplyPriceMapper.getHqSplyPriceExcelUploadSampleList */ SELECT thp.PROD_CD, thp.PROD_NM, thp.PROD_CLASS_CD, -- 상품분류코드 thpsp.SALE_UPRC AS HQ_SALE_UPRC, -- 본사판매가 thp.SPLY_UPRC AS HQ_SPLY_UPRC, -- 대표공급가 thp.SPLY_UPRC AS SPLY_UPRC -- 변경공급가 FROM TB_HQ_PRODUCT thp, ( SELECT HQ_OFFICE_CD , PROD_CD , MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) AND thpsp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpsp.PROD_CD = thp.PROD_CD ORDER BY thp.PROD_CD

mapper.delete:
/* HqSplyPriceMapper.deleteSplyPriceExcelUploadCheckAll */
DELETE
TB_TMP_EXCEL_SPLY_PRICE
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{hqOfficeCd}

/* HqSplyPriceMapper.deleteSplyPriceExcelUploadCheck */
DELETE
TB_TMP_EXCEL_SPLY_PRICE
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND PROD_CD = #{prodCd}
AND SEQ = #{seq}

mapper.select:
/* HqSplyPriceMapper.getSalePriceExcelUploadCheckList */ SELECT A.RESULT, A.PROD_CD, thp.PROD_NM, thpsp.SALE_UPRC AS HQ_SALE_UPRC, -- 본사판매가 thp.SPLY_UPRC AS HQ_SPLY_UPRC, -- 대표공급가 A.SPLY_UPRC AS SPLY_UPRC, -- 변경공급가 thp.PRC_CTRL_FG, A.SEQ FROM TB_TMP_EXCEL_SPLY_PRICE A, TB_HQ_PRODUCT thp, ( SELECT HQ_OFFICE_CD , PROD_CD , MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp WHERE 1=1 AND A.SESSION_ID = #{sessionId} AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD (+)= A.HQ_OFFICE_CD AND thp.PROD_CD (+)= A.PROD_CD AND thpsp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpsp.PROD_CD = thp.PROD_CD ORDER BY A.SEQ

/* HqSplyPriceMapper.getProdCdChk */
SELECT COUNT(*)
FROM TB_HQ_PRODUCT
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND PROD_CD = #{prodCd}
