mapper.select:
/* SalePriceMapper.getProdSalePriceList */
SELECT tms.STORE_CD, tms.STORE_NM, tmp.PROD_CD, tmp.PROD_NM, tmp.COST_UPRC, -- 원가단가 tmp.LAST_COST_UPRC, -- 최종원가단가 tmp.SPLY_UPRC, -- 공급가 tmp.SPLY_UPRC AS STORE_SPLY_UPRC, -- 매장공급가 tmpsp.SALE_UPRC AS SALE_UPRC_P, -- 변경전 판매금액 tmpsp.SALE_UPRC, -- 판매금액(변경가능) tmpsp.STIN_SALE_UPRC AS STIN_SALE_UPRC_P, -- 변경전 내점판매금액 tmpsp.STIN_SALE_UPRC, -- 내점판매금액(변경가능) tmpsp.DLVR_SALE_UPRC AS DLVR_SALE_UPRC_P, -- 변경전 배달판매금액 tmpsp.DLVR_SALE_UPRC, -- 배달판매금액(변경가능) tmpsp.PACK_SALE_UPRC AS PACK_SALE_UPRC_P, -- 변경전 포장판매금액 tmpsp.PACK_SALE_UPRC, -- 포장판매금액(변경가능) tmp.PO_UNIT_QTY, -- 주문(발주)단위수량 tmp.PRC_CTRL_FG, thpsp.SALE_UPRC AS HQ_SALE_UPRC, -- 본사판매가 thpsp.STIN_SALE_UPRC AS HQ_STIN_SALE_UPRC, -- 본사내점-판매가 thpsp.DLVR_SALE_UPRC AS HQ_DLVR_SALE_UPRC, -- 본사배달-판매가 thpsp.PACK_SALE_UPRC AS HQ_PACK_SALE_UPRC -- 본사포장-판매가
FROM TB_MS_PRODUCT tmp, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, ( SELECT STORE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC, MIN(STIN_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS STIN_SALE_UPRC, MIN(DLVR_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS DLVR_SALE_UPRC, MIN(PACK_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS PACK_SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY STORE_CD, PROD_CD ) tmpsp, TB_HQ_PRODUCT thp, ( SELECT HQ_OFFICE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC, MIN(STIN_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS STIN_SALE_UPRC, MIN(DLVR_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS DLVR_SALE_UPRC, MIN(PACK_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS PACK_SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp WHERE tmp.PROD_CD = #{prodCd} AND tmp.SALE_PROD_YN = 'Y' -- 판매상품 여부 AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tmp.STORE_CD AND tms.STORE_CD = tmsi.STORE_CD(+) AND tmpsp.STORE_CD (+)= tmp.STORE_CD AND tmpsp.PROD_CD (+)= tmp.PROD_CD AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = tmp.PROD_CD AND thpsp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpsp.PROD_CD = thp.PROD_CD
AND tms.STORE_CD IN
#{item}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
ORDER BY tms.STORE_CD, tmp.PROD_CD

/* USE : SalePriceMapper.getRegistProdCount */
SELECT COUNT(SALE_UPRC) AS CNT
  FROM TB_MS_PRODUCT_SALE_PRICE
 WHERE STORE_CD = #{storeCd}
   AND PROD_CD = #{prodCd}
   AND SALE_PRC_FG = '1'
   AND START_DATE = #{startDate}

/* USE : SalePriceMapper.getRegistHqProdCount */
SELECT COUNT(SALE_UPRC) AS CNT
FROM TB_HQ_PRODUCT_SALE_PRICE
WHERE HQ_OFFICE_CD = #{hqOfficeCd}
  AND PROD_CD = #{prodCd}
  AND SALE_PRC_FG = '1'
  AND START_DATE = #{startDate}

/* SalePriceMapper.getHqSalePriceExcelUploadSampleList */
SELECT thp.PROD_CD
FROM TB_HQ_PRODUCT thp WHERE 1=1 AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY thp.PROD_CD

mapper.delete:
/* SalePriceMapper.getSalePriceExcelUploadCheckDeleteAll */
DELETE
TB_TMP_EXCEL_SALE_PRICE
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{hqOfficeCd}

/* SalePriceMapper.getSalePriceExcelUploadCheckDelete */
DELETE
TB_TMP_EXCEL_SALE_PRICE
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND PROD_CD = #{prodCd}
AND SEQ = #{seq}

mapper.select:
/* SalePriceMapper.getProdCdChk */
SELECT COUNT(*)
FROM TB_HQ_PRODUCT
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND PROD_CD = #{prodCd}
