mapper.insert:
/* USE : StoreChgCostPriceMapper.saveStoreCostPrice */
MERGE INTO TB_MS_PRODUCT tmp
USING DUAL
ON (
        tmp.STORE_CD = #{storeCd}
        AND tmp.PROD_CD = #{prodCd}
    )
WHEN MATCHED THEN
UPDATE
SET     tmp.COST_UPRC = #{costUprc}   -- 원가
,       tmp.MOD_DT = #{modDt}
,       tmp.MOD_ID = #{modId}

/* USE : StoreChgCostPriceMapper.saveStoreIostockCostPrice */
MERGE INTO TB_ST_STORE_STOCK_MONTHLY tsssm
USING DUAL
ON (
        tsssm.STORE_CD = #{storeCd}
        AND tsssm.STORAGE_CD = #{storageCd}
        AND tsssm.IOSTOCK_YM = #{iostockYm}
        AND tsssm.PROD_CD = #{prodCd}
    )
WHEN MATCHED THEN
UPDATE
SET     tsssm.AVG_COST_UPRC = #{costUprc}   -- 원가
,       tsssm.MOD_DT = #{modDt}

/* USE : StoreChgCostPriceMapper.saveStoreIostockCostPriceHistory */
INSERT INTO TB_ST_STORE_STOCK_MONTHLY_AVG_COST_UPRC_HIST
(
   STORE_CD
,  IOSTOCK_YM
,  PROD_CD
,  PROC_DT
,  PROC_FG
,  B_AVG_COST_UPRC
,  A_AVG_COST_UPRC
,  MOD_ID
)
VALUES
(
   #{storeCd}
,  #{iostockYm}
,  #{prodCd}
,  TO_CHAR(SYSTIMESTAMP,'YYYYMMDDHH24MISSFF')
,  #{procFg}
,  NVL(#{storeCostUprc}, 0)
,  NVL(#{costUprc}, 0)
,  #{modId}
)

