mapper.select:
/* StoreChgCostPriceMapper.getStoreProdCdChk */
SELECT COUNT(*)
FROM TB_MS_PRODUCT
WHERE 1=1
AND STORE_CD = #{storeCd}
AND PROD_CD = #{prodCd}

/* StoreChgCostPriceMapper.getStoreIostockProdCdChk */
SELECT COUNT(*)
FROM TB_ST_STORE_STOCK_MONTHLY
WHERE 1=1
AND STORE_CD = #{storeCd}
AND STORAGE_CD = #{storageCd}
AND IOSTOCK_YM = #{iostockYm}
AND PROD_CD = #{prodCd}
