mapper.select:
/* StoreSplyPriceMapper.getByProdSplyPriceList */
SELECT tms.STORE_CD , tms.STORE_NM , tmp.PROD_CD , tmp.PROD_NM , thpsp.SALE_UPRC AS HQ_SALE_UPRC , tmpsp.SALE_UPRC AS STORE_SALE_UPRC , thp.SPLY_UPRC AS HQ_SPLY_UPRC , tmp.SPLY_UPRC AS STORE_SPLY_UPRC , tmp.SPLY_UPRC AS SPLY_UPRC , tmp.PRC_CTRL_FG
FROM TB_MS_PRODUCT tmp , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi , ( SELECT STORE_CD , PROD_CD , MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY STORE_CD , PROD_CD ) tmpsp , TB_HQ_PRODUCT thp , ( SELECT HQ_OFFICE_CD , PROD_CD , MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY HQ_OFFICE_CD , PROD_CD ) thpsp WHERE tmp.PROD_CD = #{prodCd} -- 판매상품 여부 AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tmp.STORE_CD AND tms.STORE_CD = tmsi.STORE_CD(+) AND tmpsp.STORE_CD (+)= tmp.STORE_CD AND tmpsp.PROD_CD (+)= tmp.PROD_CD AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = tmp.PROD_CD AND thpsp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpsp.PROD_CD = thp.PROD_CD
AND tms.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (tmp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY tms.STORE_CD , tmp.PROD_CD

