AND SALE_PRC_FG = '1' GROUP BY STORE_CD , PROD_CD ) tmpsp , TB_HQ_PRODUCT thp , ( SELECT HQ_OFFICE_CD , PROD_CD , MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1 = 1
