mapper.select:
/* USE : DlvrProdMultiMapper.getDlvrProdMultiNmExcelList */
SELECT thpdpnm.SEQ, thp.PROD_CD, thp.PROD_NM, thp.USE_YN,
thpdpnm.DLVR_PROD_NM_${item}
FROM TB_HQ_PRODUCT thp, ( SELECT * FROM ( SELECT SEQ, HQ_OFFICE_CD, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM FROM TB_HQ_PRODUCT_DLVR_PROD_NM_MULTI ) PIVOT ( MIN(DLVR_PROD_NM) FOR DLVR_NAME_CD IN (${pivotDlvrCol}) ) ) thpdpnm WHERE thpdpnm.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thpdpnm.PROD_CD (+) = thp.PROD_CD AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY thp.PROD_CD ASC
SELECT tmpdpnm.SEQ, tmp.PROD_CD, tmp.PROD_NM, tmp.USE_YN,
tmpdpnm.DLVR_PROD_NM_${item}
FROM TB_MS_PRODUCT tmp, ( SELECT * FROM ( SELECT SEQ, STORE_CD, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM FROM TB_MS_PRODUCT_DLVR_PROD_NM_MULTI ) PIVOT ( MIN(DLVR_PROD_NM) FOR DLVR_NAME_CD IN (${pivotDlvrCol}) ) ) tmpdpnm WHERE tmpdpnm.STORE_CD (+)= tmp.STORE_CD AND tmpdpnm.PROD_CD (+) = tmp.PROD_CD AND tmp.STORE_CD = #{storeCd} AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (tmp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY tmp.PROD_CD ASC

mapper.delete:
/* DlvrProdMultiMapper.deleteDlvrProdMultiNm */



        DELETE
